/** 出荷要望入力_一覧 件数チェック **/
SELECT COUNT(1) CNT FROM ( SELECT 1 FROM MDOT0030 T1 WHERE
/** 申請DB検索の有効可否を設定 **/
AND T1.SHINSEI_FLG = 0
UNION ALL SELECT 1 FROM MDOT0040 T1 WHERE
/** 確定DB検索の有効可否を設定 **/
)
/** 出荷要望入力_一覧 検索 **/
SELECT T1.TENPO_CD AS TENPO_CD /* 0 店舗コード */ , T1.KANRI_NO AS KANRI_NO /* 1 管理№ */ , T1.SYORI_YMD AS SYORI_YMD /* 2 処理日付 */ , T1.SYORI_TM AS SYORI_TM /* 3 処理時間 */ , T1.IRAIRIYU_CD AS IRAIRIYU_CD /* 4 依頼理由コード */ , M2.RIYUCOMMENT_NM AS RIYUCOMMENT_NM /* 5 依頼理由名称 */ , T1.IRAIGOKEI_SU AS IRAIGOKEI_SU /* 6 合計依頼数量 */ , T1.IRAIGOKEI_KIN AS IRAIGOKEI_KIN /* 7 合計依頼金額 */ , 0 AS APPLY_YMD /* 8 申請日 */ , T1.SHINSEI_FLG AS SHINSEI_FLG /* 9 申請状態 */ , M1.MEISYO_NM AS MEISYO_NM2 /* 10 申請状態名称 */ , T1.ADD_YMD AS ADD_YMD /* 11 登録日 */ , T1.ADD_TM AS ADD_TM /* 12 登録時間 */ , T1.ADDTAN_CD AS ADDTAN_CD /* 13 登録担当者コード */ , T1.UPD_YMD AS UPD_YMD /* 14 更新日 */ , T1.UPD_TM AS UPD_TM /* 15 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 16 更新担当者コード */ , M3.HANBAIIN_NM AS HANBAIIN_NM /* 17 更新担当者名 */ , T1.SAKUJYO_YMD AS SAKUJYO_YMD /* 18 削除日 */ , T1.SAKUJYO_FLG AS SAKUJYO_FLG /* 19 削除フラグ */ , 0 AS SOSINZUMI_FLG /* 20 送信済フラグ */ , T1.HHTSERIAL_NO AS HHTSERIAL_NO /* 21 HHTシリアル番号 */ , T1.HHTSEQUENCE_NO AS HHTSEQUENCE_NO /* 22 HHTシーケンスNo. */ FROM MDOT0030 T1 /* 出荷要望申請TBL(H) */ , MDMT0100 M1 /* 名称MST(申請状態名称) */ , MDMT0180 M2 /* 理由コメントMST */ , BOMT0030 M3 /* 販売員MST */ WHERE
/** 申請DB検索の有効可否を設定 **/
AND M1.SIKIBETSU_CD(+) = 'HTST' AND T1.SHINSEI_FLG = M1.MEISYO_CD(+) AND M2.GYOMU_KB(+) = '013' AND T1.IRAIRIYU_CD = M2.RIYU_CD(+) AND T1.UPD_TANCD = M3.HANBAIIN_CD(+) AND T1.SHINSEI_FLG = 0
UNION ALL SELECT T1.TENPO_CD AS TENPO_CD /* 0 店舗コード */ , T1.KANRI_NO AS KANRI_NO /* 1 管理№ */ , T1.SYORI_YMD AS SYORI_YMD /* 2 処理日付 */ , T1.SYORI_TM AS SYORI_TM /* 3 処理時間 */ , T1.IRAIRIYU_CD AS IRAIRIYU_CD /* 4 依頼理由コード */ , M2.RIYUCOMMENT_NM AS RIYUCOMMENT_NM /* 5 依頼理由名称 */ , T1.IRAIGOKEI_SU AS IRAIGOKEI_SU /* 6 合計依頼数量 */ , T1.IRAIGOKEI_KIN AS IRAIGOKEI_KIN /* 7 合計依頼金額 */ , T1.APPLY_YMD AS APPLY_YMD /* 8 申請日 */ , 1 AS SHINSEI_FLG /* 9 申請状態 */ , M1.MEISYO_NM AS MEISYO_NM2 /* 10 申請状態名称 */ , T1.ADD_YMD AS ADD_YMD /* 11 登録日 */ , T1.ADD_TM AS ADD_TM /* 12 登録時間 */ , T1.ADDTAN_CD AS ADDTAN_CD /* 13 登録担当者コード */ , T1.UPD_YMD AS UPD_YMD /* 14 更新日 */ , T1.UPD_TM AS UPD_TM /* 15 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 16 更新担当者コード */ , M3.HANBAIIN_NM AS HANBAIIN_NM /* 17 更新担当者名 */ , T1.SAKUJYO_YMD AS SAKUJYO_YMD /* 18 削除日 */ , T1.SAKUJYO_FLG AS SAKUJYO_FLG /* 19 削除フラグ */ , T1.SOSINZUMI_FLG AS SOSINZUMI_FLG /* 20 送信済フラグ */ , T1.HHTSERIAL_NO AS HHTSERIAL_NO /* 21 HHTシリアル番号 */ , T1.HHTSEQUENCE_NO AS HHTSEQUENCE_NO /* 22 HHTシーケンスNo. */ FROM MDOT0040 T1 /* 出荷要望確定TBL(H) */ , MDMT0100 M1 /* 名称MST(申請状態名称) */ , MDMT0180 M2 /* 理由コメントMST */ , BOMT0030 M3 /* 販売員MST */ WHERE
/** 確定DB検索の有効可否を設定 **/
AND M1.SIKIBETSU_CD(+) = 'HTST' AND M1.MEISYO_CD(+) = 1 AND M2.GYOMU_KB(+) = '013' AND T1.IRAIRIYU_CD = M2.RIYU_CD(+) AND T1.UPD_TANCD = M3.HANBAIIN_CD
ORDER BY UPD_YMD DESC /* 16 更新日 */ , UPD_TM DESC /* 17 更新時間 */ , KANRI_NO DESC /* 1 管理№ */
/** 出荷要望入力_一覧 明細（未申請） **/
SELECT T2.KANRI_NO AS KANRI_NO /* 管理№ */ ,T2.GYO_NO AS GYO_NO /* 行No */ ,T2.SYORI_YMD AS SYORI_YMD /* 処理日付 */ ,T2.SYORI_TM AS SYORI_TM /* 処理時間 */ ,T2.BUMON_CD AS BUMON_CD /* 部門コード */ ,M1.BUMON_NM AS BUMON_NM /* 部門名 */ ,M1.BUMONKANA_NM AS BUMONKANA_NM /* 部門名カナ */ ,T2.HINSYU_CD AS HINSYU_CD /* 品種コード */ ,M2.HINSYU_RYAKU_NM AS HINSYU_RYAKU_NM /* 品種名 */ ,T2.BURANDO_CD AS BURANDO_CD /* ブランドコード */ ,M3.BURANDO_NMK AS BURANDO_NMK /* ブランド名 */ ,T2.SYONMK AS SYONMK /* 商品名 */ ,T2.HIN_NBR AS HIN_NBR /* メーカー品番 */ ,T2.JISYA_HBN AS JISYA_HBN /* 自社品番 */ ,T2.JAN_CD AS JAN_CD /* JANコード */ ,T2.IRO_CD AS IRO_CD /* 色コード */ ,M4.IRO_NM AS IRO_NM /* 色名 */ ,T2.SIZE_CD AS SIZE_CD /* サイズコード */ ,T2.SIZE_NM AS SIZE_NM /* サイズ名 */ ,T3.HYOKA_KB AS HYOKA_KB /* 評価区分 */ ,HYOKA.MEISYO_NM AS HYOKA_NM /* 評価名称 */ ,M5.HATYUTAISYO_KB AS HATYUTAISYO_KB /* 補充区分 */ ,HOJU.MEISYO_NM AS HOJU_NM /* 補充名称 */ ,M5.HANBAIKANRYO_YMD AS HANBAIKANRYO_YMD /* 販売完了日 */ ,( SELECT /*+ INDEX(MDBT0030 MDBT0030_PRIMARY) */ NVL(SUM(MDBT0030.FREEZAIKO_SU), 0) FROM MDBT0030 /* 前日在庫テーブル */ WHERE MDBT0030.TENPO_CD = T2.TENPO_CD AND T2.JAN_CD = MDBT0030.JAN_CD ) + ( SELECT /*+ INDEX(MDBT0040 MDBT0040_INDEX3) */ NVL(SUM(MDBT0040.FREEZAIKO_SU), 0) FROM MDBT0040 /* 当日受払テーブル */ WHERE MDBT0040.TENPO_CD = T2.TENPO_CD AND T2.JAN_CD = MDBT0040.JAN_CD ) + ( SELECT /*+ INDEX(MDBT0060 MDBT0060_PRIMARY) */ NVL(SUM(MDBT0060.FREEZAIKO_SU), 0) FROM MDBT0060 /* 受払履歴テーブル */ WHERE MDBT0060.TENPO_CD = T2.TENPO_CD AND T2.JAN_CD = MDBT0060.JAN_CD ) AS TENZAIKO_SU /* 店在庫 */ ,( SELECT NVL(SUM(MDPT0011.YOTEI_SU),0) FROM MDPT0011 WHERE MDPT0011.TENPO_CD = T2.TENPO_CD AND T2.JAN_CD = MDPT0011.JAN_CD AND MDPT0011.KAKUTEI_FLG = 0 ) + ( SELECT NVL(SUM(MDNT0011.NYUKAYOTEI_SU),0) FROM MDNT0010 INNER JOIN MDNT0011 ON MDNT0010.TENPOLC_KBN = MDNT0011.TENPOLC_KBN AND MDNT0010.SYUKKAKAISYA_CD = MDNT0011.SYUKKAKAISYA_CD AND MDNT0010.SYUKKATEN_CD = MDNT0011.SYUKKATEN_CD AND MDNT0010.DENPYO_BANGO = MDNT0011.DENPYO_BANGO WHERE MDNT0010.JYURYOKAISYA_CD = MDGETOWNCOPM -- 自社 AND MDNT0010.JYURYOTEN_CD = T2.TENPO_CD AND T2.JAN_CD = MDNT0011.JAN_CD AND MDNT0010.KAKUTEI_FLG = 0 ) AS NYUKAYOTEI_SU /* 入荷予定数 */ ,( SELECT NVL(SUM(MDBT0070.URIAGE_SU), 0) FROM MDBT0070 /* 売上数累積テーブル */ WHERE MDBT0070.TENPO_CD = T2.TENPO_CD AND T2.JAN_CD = MDBT0070.JAN_CD AND MDBT0070.SYU_CD BETWEEN MDADDSYUCD(MDGETSYUCD(TO_CHAR(SYSDATE, 'YYYYMMDD')), -3) AND MDGETSYUCD(TO_CHAR(SYSDATE, 'YYYYMMDD')) ) AS URIAGE_SU /* 売上実績数 */ ,M6.JIDO_SU AS JIDO_SU /* 自動定数 */ ,T2.IRAI_SU /* 数量 */ ,T2.IRAI_KIN /* 金額 */ ,T2.GEN_TNK /* 原単価 */ ,T2.SYOHIN_CD /* 商品コード */ ,0 AS MESSEGE_KB /* メッセージ区分(画面側で算出) */ ,'' AS MESSEGE_NM /* メッセージ名称(画面側で算出) */ ,M5.SYOHIN_ZOKUSEI AS SYOHIN_ZOKUSEI /* 商品属性 */ FROM MDOT0030 T1 /* 出荷要望申請H */ ,MDOT0031 T2 /* 出荷要望申請B */ ,BOMT0060 M1 /* 部門マスタ */ ,MDMT0070 M2 /* 品種マスタ */ ,STMT0500 M3 /* ブランドマスタ */ ,MDMT0080 M4 /* 色マスタ */ ,MDBT0030 T3 /* 前日在庫(評価) */ ,MDMT0100 HYOKA /* 名称マスタ(評価) */ ,MDMT0100 HOJU /* 名称マスタ(補充) */ ,MDMT0130 M5 /* 発注マスタ */ ,MDMT0170 M6 /* 自動定数マスタ */ ,MDMT0020 M7 /* 仕入先マスタ */ ,MDMT0100 MESSAGE /* 名称マスタ(メッセージ名称) */ WHERE T1.TENPO_CD = T2.TENPO_CD AND T1.KANRI_NO = T2.KANRI_NO AND T1.SYORI_YMD = T2.SYORI_YMD AND T2.BUMON_CD = M1.BUMON_CD(+) AND T2.BUMON_CD = M2.BUMON_CD(+) AND T2.HINSYU_CD = M2.HINSYU_CD(+) AND T2.BURANDO_CD = M3.BURANDO_CD(+) AND T2.IRO_CD = M4.IRO_CD(+) AND T2.IRO_CD = M4.IRO_CD(+) AND T2.TENPO_CD = T3.TENPO_CD(+) AND T2.JAN_CD = T3.JAN_CD(+) AND HYOKA.SIKIBETSU_CD(+) = 'HTHK' -- 評価 AND T3.HYOKA_KB = HYOKA.MEISYO_CD(+) AND HOJU.SIKIBETSU_CD(+) = 'HTTS' -- 補充 AND M5.HATYUTAISYO_KB = HOJU.MEISYO_CD(+) AND T2.JAN_CD = M5.JAN_CD(+) AND T2.TENPO_CD = M6.TENPO_CD(+) AND T2.JAN_CD = M6.JAN_CD(+) AND M5.SIIRESAKI_CD = M7.SIIRESAKI_CD(+) AND MESSAGE.SIKIBETSU_CD(+) = 'HTMS' -- メッセージ AND T3.HYOKA_KB = MESSAGE.MEISYO_CD(+) AND T1.TENPO_CD = AND T1.KANRI_NO = AND T1.SYORI_YMD = ORDER BY T2.KANRI_NO /* 管理№ */ ,T2.GYO_NO /* 行No */
