/** 補充依頼結果(H) 件数チェック **/
SELECT COUNT(*) CNT FROM MDOT0060 T1 WHERE 0=0
/** 補充依頼結果(H) 検索 **/
SELECT M2.MEISYO_NM ,T1.BUMON_CD ,M1.BUMONKANA_NM ,T1.IRAI_SU ,T1.HAIBUN_SU ,T1.HAIBUN_KIN ,T1.KESSAI_YMD ,T1.HENKO_KBN ,M1.BUMON_NM FROM MDOT0060 T1 LEFT JOIN BOMT0060 M1 ON M1.BUMON_CD = T1.BUMON_CD LEFT JOIN MDMT0100 M2 ON M2.SIKIBETSU_CD = 'CHNG' AND M2.MEISYO_CD = T1.HENKO_KBN WHERE 0=0
ORDER BY T1.BUMON_CD ,T1.HENKO_KBN ,T1.KESSAI_YMD
/** 補充依頼結果(B) 検索 **/
SELECT M1.HINSYU_RYAKU_NM ,M2.BURANDO_NMK ,T1.JISYA_HBN ,M5.SYOHIN_ZOKUSEI ,T1.JAN_CD ,T1.HIN_NBR ,T1.SYONMK ,M3.IRO_NM ,T1.SIZE_NM ,M4.MEISYO_NM ,T1.HANBAIKANRYO_YMD ,T1.IRAI_SU ,T1.HAIBUN_SU ,T1.GEN_TNK ,T1.COMMENT_NM FROM MDOT0061 T1 LEFT JOIN MDMT0070 M1 ON M1.BUMON_CD = T1.BUMON_CD AND M1.HINSYU_CD = T1.HINSYU_CD LEFT JOIN STMT0500 M2 ON M2.BURANDO_CD = T1.BURANDO_CD LEFT JOIN MDMT0080 M3 ON M3.IRO_CD = T1.IRO_CD LEFT JOIN MDMT0100 M4 ON M4.SIKIBETSU_CD = 'HTSN' AND M4.MEISYO_CD = T1.SEASON_KB LEFT JOIN MDMT0130 M5 ON M5.JAN_CD = T1.JAN_CD WHERE T1.TENPO_CD = AND T1.KESSAI_YMD = AND T1.HENKO_KBN = AND T1.BUMON_CD = ORDER BY T1.HINSYU_CD ,T1.BURANDO_CD ,T1.JISYA_HBN ,T1.IRO_CD ,T1.SIZE_CD
