/** 自動定数変更 件数チェック **/
SELECT COUNT(1) CNT FROM ( SELECT 1 FROM MDOT0050 T1 /* 自動定数依頼TBL */ WHERE
/** 依頼DB検索の有効可否を設定 **/
UNION ALL SELECT 1 FROM MDMT0170 T1 /* 自動定数マスタ */ , MDMT0130 T2 /* 発注マスタ */ WHERE
/** マスタDB検索の有効可否を設定 **/
AND T1.JAN_CD = T2.JAN_CD
)
/** 自動定数変更 検索 **/
SELECT T1.TENPO_CD AS TENPO_CD /* 0 店舗コード */ , T1.SYORI_YMD AS SYORI_YMD /* 1 処理日付 */ , T1.SYORI_TM AS SYORI_TM /* 2 処理時間 */ , T1.BUMON_CD AS BUMON_CD /* 3 部門コード */ , M1.BUMON_NM AS BUMON_NM /* 4 部門名 */ , M1.BUMONKANA_NM AS BUMONKANA_NM /* 5 部門名カナ */ , T1.HINSYU_CD AS HINSYU_CD /* 6 品種コード */ , M2.HINSYU_RYAKU_NM AS HINSYU_RYAKU_NM /* 7 品種名 */ , T1.BURANDO_CD AS BURANDO_CD /* 8 ブランドコード */ , M3.BURANDO_NMK AS BURANDO_NMK /* 9 ブランド名 */ , T1.JISYA_HBN AS XEBIO_CD /* 10 自社品番 */ , T1.HIN_NBR AS HIN_NBR /* 11 メーカー品番 */ , T1.SYONMK AS SYONMK /* 12 商品名カナ */ , T1.JAN_CD AS JAN_CD /* 13 JANコード */ , T1.SYOHIN_CD AS SYOHIN_CD /* 14 商品コード */ , T1.IRO_CD AS IRO_CD /* 15 色コード */ , M4.IRO_NM AS IRO_NM /* 16 色名 */ , T1.SIZE_CD AS SIZE_CD /* 17 サイズコード */ , T1.SIZE_NM AS SIZE_NM /* 18 サイズ名 */ , T1.GEN_TNK AS GEN_TNK /* 19 原価 */ , T1.KAISI_YMD AS KAISI_YMD /* 20 開始日 */ , T1.SYURYO_YMD AS SYURYO_YMD /* 21 終了日 */ , T1.HATTYUPTN_KBN AS HATTYUPTN_KBN /* 22 発注パターン区分 */ , HATTYU.MEISYO_NM AS HATTYUPTN_NM /* 23 発注パターン名称 */ , T1.JIDO_KBN AS JIDO_KBN /* 24 自動区分 */ , JIDOKB.MEISYO_NM AS JIDO_KBN_NM /* 25 自動区分名称 */ , T1.URIAGE_SU AS URI_SU /* 26 売上実績 */ , T1.GENZAISETTEI_SU AS GENZAISETTEI_SU /* 27 現在設定 */ , T1.IRAI_SU AS IRAI_SU /* 28 変更依頼 */ , T1.LOT_SU AS LOT_SU /* 29 ロット数 */ , T1.IRAIRIYU_CD AS IRAIRIYU_CD /* 30 依頼理由コード */ , M6.RIYUCOMMENT_NM AS RIYUCOMMENT_NM /* 31 依頼理由名称 */ , T1.UPD_TANCD AS JIDOTESU_UPD /* 32 担当者コード */ , M5.HANBAIIN_NM AS JIDOTESU_UPD_TANNM /* 33 担当者名 */ , T1.ADD_YMD AS ADD_YMD /* 34 登録日 */ , 0 AS ADD_KBN /* 35 登録区分 */ , ADDKB.MEISYO_NM AS ADD_KBN_NM /* 36 登録区分名称 */ , T1.UPD_YMD AS UPD_YMD /* 37 更新日 */ , T1.UPD_TM AS UPD_TM /* 38 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 39 更新担当者コード */ , (SELECT S_MDMT0130_1.MOTOMIYASYOHIN_FLG FROM MDMT0130 S_MDMT0130_1 WHERE S_MDMT0130_1.JAN_CD = T1.JAN_CD ) AS MOTOMIYASYOHIN_FLG /* 40 本宮商品フラグ */ , (SELECT S_MDMT0130_2.SYOHIN_ZOKUSEI FROM MDMT0130 S_MDMT0130_2 WHERE S_MDMT0130_2.JAN_CD = T1.JAN_CD ) AS SYOHIN_ZOKUSEI /* 41 商品属性 */ , T1.SOSINZUMI_FLG AS SOSINZUMI_FLG /* 42 送信済フラグ */ FROM MDOT0050 T1 /* 自動定数依頼TBL */ , BOMT0060 M1 /* 部門マスタ */ , MDMT0070 M2 /* 品種マスタ */ , STMT0500 M3 /* ブランドマスタ */ , MDMT0080 M4 /* 色マスタ */ , BOMT0030 M5 /* 販売員マスタ */ , MDMT0180 M6 /* 理由マスタ */ , MDMT0100 HATTYU /* 名称マスタ(発注パターン) */ , MDMT0100 JIDOKB /* 名称マスタ(自動区分) */ , MDMT0100 ADDKB /* 名称マスタ(登録区分) */ WHERE
/** 依頼DB検索の有効可否を設定 **/
AND T1.BUMON_CD = M1.BUMON_CD(+) AND T1.BUMON_CD = M2.BUMON_CD(+) AND T1.HINSYU_CD = M2.HINSYU_CD(+) AND T1.BURANDO_CD = M3.BURANDO_CD(+) AND T1.IRO_CD = M4.IRO_CD(+) AND HATTYU.SIKIBETSU_CD(+) = 'HTPT' AND T1.HATTYUPTN_KBN = HATTYU.MEISYO_CD(+) AND JIDOKB.SIKIBETSU_CD(+) = 'HTJK' AND T1.JIDO_KBN = JIDOKB.MEISYO_CD(+) AND M6.GYOMU_KB(+) = '014' AND T1.IRAIRIYU_CD = M6.RIYU_CD(+) AND T1.UPD_TANCD = M5.HANBAIIN_CD(+) AND ADDKB.SIKIBETSU_CD(+) = 'HTTK' AND ADDKB.MEISYO_CD(+) = 0
UNION ALL SELECT T1.TENPO_CD AS TENPO_CD /* 0 店舗コード */ , TO_NUMBER(TO_CHAR(SYSDATE, 'YYYYMMDD')) AS SYORI_YMD /* 1 処理日付 */ , 999999999 AS SYORI_TM /* 2 処理時間 */ , T2.BUMON_CD AS BUMON_CD /* 3 部門コード */ , M1.BUMON_NM AS BUMON_NM /* 4 部門名 */ , M1.BUMONKANA_NM AS BUMONKANA_NM /* 5 部門名カナ */ , T2.HINSYU_CD AS HINSYU_CD /* 6 品種コード */ , M2.HINSYU_RYAKU_NM AS HINSYU_RYAKU_NM /* 7 品種名 */ , T2.BURANDO_CD AS BURANDO_CD /* 8 ブランドコード */ , M3.BURANDO_NMK AS BURANDO_NMK /* 9 ブランド名 */ , T2.XEBIO_CD AS XEBIO_CD /* 10 自社品番 */ , T2.HIN_NBR AS HIN_NBR /* 11 メーカー品番 */ , T2.SYONMK AS SYONMK /* 12 商品名カナ */ , T1.JAN_CD AS JAN_CD /* 13 JANコード */ , T2.SYOHIN_CD AS SYOHIN_CD /* 14 商品コード */ , T2.MAKERCOLOR_CD AS IRO_CD /* 15 色コード */ , M4.IRO_NM AS IRO_NM /* 16 色名 */ , T2.SIZE_NM AS SIZE_CD /* 17 サイズコード */ , M7.SIZE_NM AS SIZE_NM /* 18 サイズ名 */ , T2.GENKA AS GEN_TNK /* 19 原価 */ , T1.KAISI_YMD AS KAISI_YMD /* 20 開始日 */ , T1.SYURYO_YMD AS SYURYO_YMD /* 21 終了日 */ , T1.HATTYUPTN_KBN AS HATTYUPTN_KBN /* 22 発注パターン区分 */ , HATTYU.MEISYO_NM AS HATTYUPTN_NM /* 23 発注パターン名称 */ , T1.JIDO_KBN AS JIDO_KBN /* 24 自動区分 */ , JIDOKB.MEISYO_NM AS JIDO_KBN_NM /* 25 自動区分名称 */ , ( SELECT NVL(SUM(MDBT0070.URIAGE_SU), 0) FROM MDBT0070 /* 売上数累積テーブル */ WHERE MDBT0070.TENPO_CD = T1.TENPO_CD AND MDBT0070.JAN_CD = T2.JAN_CD AND MDBT0070.SYU_CD BETWEEN MDADDSYUCD(MDGETSYUCD(TO_CHAR(SYSDATE, 'YYYYMMDD')), -4) AND MDGETSYUCD(TO_CHAR(SYSDATE, 'YYYYMMDD')) ) AS URI_SU /* 26 売上実績 */ , T1.JIDO_SU AS GENZAISETTEI_SU /* 27 現在設定 */ , NULL AS IRAI_SU /* 28 変更依頼 */ , T2.LOT_SU AS LOT_SU /* 29 ロット数 */ , -1 AS IRAIRIYU_CD /* 30 依頼理由コード */ , NULL AS RIYUCOMMENT_NM /* 31 依頼理由名称 */ , T1.ADDTAN_CD AS JIDOTESU_UPD /* 32 担当者コード */ , M5.HANBAIIN_NM AS JIDOTESU_UPD_TANNM /* 33 担当者名 */ , T1.ADD_YMD AS ADD_YMD /* 34 登録日 */ , T1.ADD_KBN AS ADD_KBN /* 35 登録区分 */ , ADDKB.MEISYO_NM AS ADD_KBN_NM /* 36 登録区分名称 */ , T1.UPD_YMD AS UPD_YMD /* 37 更新日 */ , T1.UPD_TM AS UPD_TM /* 38 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 39 更新担当者コード */ , T2.MOTOMIYASYOHIN_FLG AS MOTOMIYASYOHIN_FLG /* 40 本宮商品フラグ */ , T2.SYOHIN_ZOKUSEI AS SYOHIN_ZOKUSEI /* 41 商品属性 */ , 0 AS SOSINZUMI_FLG /* 42 送信済フラグ */ FROM MDMT0170 T1 /* 自動定数マスタ */ , MDMT0130 T2 /* 発注マスタ */ , BOMT0060 M1 /* 部門マスタ */ , MDMT0070 M2 /* 品種マスタ */ , STMT0500 M3 /* ブランドマスタ */ , MDMT0080 M4 /* 色マスタ */ , BOMT0030 M5 /* 販売員マスタ */ , MDMT0090 M7 /* サイズマスタ */ , MDMT0100 HATTYU /* 名称マスタ(発注パターン) */ , MDMT0100 JIDOKB /* 名称マスタ(自動区分) */ , MDMT0100 ADDKB /* 名称マスタ(登録区分) */ WHERE
/** マスタDB検索の有効可否を設定 **/
AND T1.JAN_CD = T2.JAN_CD AND T2.BUMON_CD = M1.BUMON_CD(+) AND T2.BUMON_CD = M2.BUMON_CD(+) AND T2.HINSYU_CD = M2.HINSYU_CD(+) AND T2.BURANDO_CD = M3.BURANDO_CD(+) AND T2.MAKERCOLOR_CD = M4.IRO_CD(+) AND T2.SIZE_NM = M7.SIZE_CD(+) AND HATTYU.SIKIBETSU_CD(+) = 'HTPT' AND T1.HATTYUPTN_KBN = HATTYU.MEISYO_CD(+) AND JIDOKB.SIKIBETSU_CD(+) = 'HTJK' AND T1.JIDO_KBN = JIDOKB.MEISYO_CD(+) AND T1.ADDTAN_CD = M5.HANBAIIN_CD(+) AND ADDKB.SIKIBETSU_CD(+) = 'HTTK' AND T1.ADD_KBN = ADDKB.MEISYO_CD(+)
ORDER BY BUMON_CD /* 3 部門コード */ , HINSYU_CD /* 6 品種コード */ , BURANDO_CD /* 8 ブランドコード */ , XEBIO_CD /* 10 自社品番 */ , HIN_NBR /* 11 メーカー品番 */ , IRO_CD /* 15 色コード */ , SIZE_CD /* 17 サイズコード */ , SYORI_YMD DESC /* 1 処理日付 */ , SYORI_TM DESC /* 2 処理時間 */
/** 自動定数変更 [自動定数依頼TBL]をMERGE **/
MERGE INTO MDOT0050 /* 動定数依頼テーブル */ USING ( SELECT AS TENPO_CD /*店舗コード*/ , AS SYORI_YMD /*処理日付*/ , AS SYORI_TM /*処理時間*/ , AS KAISI_YMD /*開始日*/ , AS SYURYO_YMD /*終了日*/ , AS BUMON_CD /*部門コード*/ , AS HINSYU_CD /*品種コード*/ , AS BURANDO_CD /*ブランドコード*/ , AS JISYA_HBN /*自社品番*/ , AS IRO_CD /*色コード*/ , AS SIZE_CD /*サイズコード*/ , AS SIZE_NM /*サイズ名*/ , AS JAN_CD /*ＪＡＮコード*/ , AS SYOHIN_CD /*商品コード*/ , AS HIN_NBR /*メーカー品番*/ , AS SYONMK /*商品名(カナ)*/ , AS GEN_TNK /*原単価*/ , AS GENZAISETTEI_SU /*現在設定数*/ , AS IRAI_SU /*依頼数量*/ , AS HATTYUPTN_KBN /*発注パターン*/ , AS JIDO_KBN /*自動区分*/ , AS URIAGE_SU /*売上実績数*/ , AS IRAIRIYU_CD /*依頼理由コード*/ , AS LOT_SU /*ロット数*/ , AS ADD_YMD /*登録日*/ , AS ADD_TM /*登録時間*/ , AS ADDTAN_CD /*登録担当者コード*/ , AS UPD_YMD /*更新日*/ , AS UPD_TM /*更新時間*/ , AS UPD_TANCD /*更新担当者コード*/ , AS SAKUJYO_YMD /*削除日*/ , AS SAKUJYO_FLG /*削除フラグ*/ , AS SOSINIRAI_FLG /*送信依頼フラグ*/ , AS SOSINZUMI_FLG /*送信済フラグ*/ , AS SOSIN_YMD /*送信日*/ , AS SOSIN_TM /*送信時間*/ FROM DUAL ) UPDVAL ON( MDOT0050.TENPO_CD = UPDVAL.TENPO_CD AND MDOT0050.SYORI_YMD = UPDVAL.SYORI_YMD AND MDOT0050.JAN_CD = UPDVAL.JAN_CD) /* 既存レコードの更新 ------------------------------------------*/ WHEN MATCHED THEN UPDATE SET KAISI_YMD = UPDVAL.KAISI_YMD /* 開始日 */ ,SYURYO_YMD = UPDVAL.SYURYO_YMD /* 終了日 */ ,BUMON_CD = UPDVAL.BUMON_CD /* 部門コード */ ,HINSYU_CD = UPDVAL.HINSYU_CD /* 品種コード */ ,BURANDO_CD = UPDVAL.BURANDO_CD /* ブランドコード */ ,JISYA_HBN = UPDVAL.JISYA_HBN /* 自社品番 */ ,IRO_CD = UPDVAL.IRO_CD /* 色コード */ ,SIZE_CD = UPDVAL.SIZE_CD /* サイズコード */ ,SIZE_NM = UPDVAL.SIZE_NM /* サイズ名 */ ,SYOHIN_CD = UPDVAL.SYOHIN_CD /* 商品コード */ ,HIN_NBR = UPDVAL.HIN_NBR /* メーカー品番 */ ,SYONMK = UPDVAL.SYONMK /* 商品名(カナ) */ ,GEN_TNK = UPDVAL.GEN_TNK /* 原単価 */ ,GENZAISETTEI_SU = UPDVAL.GENZAISETTEI_SU /* 現在設定数 */ ,IRAI_SU = UPDVAL.IRAI_SU /* 依頼数量 */ ,HATTYUPTN_KBN = UPDVAL.HATTYUPTN_KBN /* 発注パターン */ ,JIDO_KBN = UPDVAL.JIDO_KBN /* 自動区分 */ ,URIAGE_SU = UPDVAL.URIAGE_SU /* 売上実績数 */ ,IRAIRIYU_CD = UPDVAL.IRAIRIYU_CD /* 依頼理由コード */ ,LOT_SU = UPDVAL.LOT_SU /* ロット数 */ ,UPD_YMD = UPDVAL.UPD_YMD /* 更新日 */ ,UPD_TM = UPDVAL.UPD_TM /* 更新時間 */ ,UPD_TANCD = UPDVAL.UPD_TANCD /* 更新担当者コード */ ,SAKUJYO_FLG = 0 /* 削除フラグ */ ,SOSINIRAI_FLG = 1 /* 送信依頼フラグ */ /* 新規レコードの作成 ------------------------------------------*/ WHEN NOT MATCHED THEN INSERT ( TENPO_CD /*店舗コード*/ ,SYORI_YMD /*処理日付*/ ,SYORI_TM /*処理時間*/ ,KAISI_YMD /*開始日*/ ,SYURYO_YMD /*終了日*/ ,BUMON_CD /*部門コード*/ ,HINSYU_CD /*品種コード*/ ,BURANDO_CD /*ブランドコード*/ ,JISYA_HBN /*自社品番*/ ,IRO_CD /*色コード*/ ,SIZE_CD /*サイズコード*/ ,SIZE_NM /*サイズ名*/ ,JAN_CD /*ＪＡＮコード*/ ,SYOHIN_CD /*商品コード*/ ,HIN_NBR /*メーカー品番*/ ,SYONMK /*商品名(カナ)*/ ,GEN_TNK /*原単価*/ ,GENZAISETTEI_SU /*現在設定数*/ ,IRAI_SU /*依頼数量*/ ,HATTYUPTN_KBN /*発注パターン*/ ,JIDO_KBN /*自動区分*/ ,URIAGE_SU /*売上実績数*/ ,IRAIRIYU_CD /*依頼理由コード*/ ,LOT_SU /*ロット数*/ ,ADD_YMD /*登録日*/ ,ADD_TM /*登録時間*/ ,ADDTAN_CD /*登録担当者コード*/ ,UPD_YMD /*更新日*/ ,UPD_TM /*更新時間*/ ,UPD_TANCD /*更新担当者コード*/ ,SAKUJYO_YMD /*削除日*/ ,SAKUJYO_FLG /*削除フラグ*/ ,SOSINIRAI_FLG /*送信依頼フラグ*/ ,SOSINZUMI_FLG /*送信済フラグ*/ ,SOSIN_YMD /*送信日*/ ,SOSIN_TM /*送信時間*/ ) VALUES ( UPDVAL.TENPO_CD /*店舗コード*/ ,UPDVAL.SYORI_YMD /*処理日付*/ ,UPDVAL.SYORI_TM /*処理時間*/ ,UPDVAL.KAISI_YMD /*開始日*/ ,UPDVAL.SYURYO_YMD /*終了日*/ ,UPDVAL.BUMON_CD /*部門コード*/ ,UPDVAL.HINSYU_CD /*品種コード*/ ,UPDVAL.BURANDO_CD /*ブランドコード*/ ,UPDVAL.JISYA_HBN /*自社品番*/ ,UPDVAL.IRO_CD /*色コード*/ ,UPDVAL.SIZE_CD /*サイズコード*/ ,UPDVAL.SIZE_NM /*サイズ名*/ ,UPDVAL.JAN_CD /*ＪＡＮコード*/ ,UPDVAL.SYOHIN_CD /*商品コード*/ ,UPDVAL.HIN_NBR /*メーカー品番*/ ,UPDVAL.SYONMK /*商品名(カナ)*/ ,UPDVAL.GEN_TNK /*原単価*/ ,UPDVAL.GENZAISETTEI_SU /*現在設定数*/ ,UPDVAL.IRAI_SU /*依頼数量*/ ,UPDVAL.HATTYUPTN_KBN /*発注パターン*/ ,UPDVAL.JIDO_KBN /*自動区分*/ ,UPDVAL.URIAGE_SU /*売上実績数*/ ,UPDVAL.IRAIRIYU_CD /*依頼理由コード*/ ,UPDVAL.LOT_SU /*ロット数*/ ,UPDVAL.ADD_YMD /*登録日*/ ,UPDVAL.ADD_TM /*登録時間*/ ,UPDVAL.ADDTAN_CD /*登録担当者コード*/ ,UPDVAL.UPD_YMD /*更新日*/ ,UPDVAL.UPD_TM /*更新時間*/ ,UPDVAL.UPD_TANCD /*更新担当者コード*/ ,UPDVAL.SAKUJYO_YMD /*削除日*/ ,0 /* 削除フラグ */ ,1 /* 送信依頼フラグ */ ,0 /* 送信済フラグ */ ,0 /* 送信日 */ ,0 /* 送信時間 */ )
