WHERE DENPYO_BANGO = /* 伝票番号 */ AND SYUKKA_YMD = /* 出荷日 */ AND SYUKKAKAISYA_CD = /* 出荷会社コー */ AND SYUKKATEN_CD = /* 出荷店コード */
