/** 移動入荷検索 件数(移動入荷予定テーブル、移動入荷確定テーブル)チェック **/
SELECT SUM(CNT) AS CNT FROM ( SELECT COUNT(*) AS CNT FROM
WHERE 0=0
UNION ALL SELECT COUNT(*) AS CNT FROM
WHERE 0=0
)
/** 移動入荷検索 件数チェック **/
SELECT COUNT(*) AS CNT FROM
WHERE 0=0
/** 移動入荷検索 検索(移動入荷予定テーブル、移動入荷確定テーブル) **/
SELECT T1.TENPOLC_KBN AS TENPOLC_KBN /* 1 店舗ＬＣ区分 */ ,T1.SYUKKAKAISYA_CD AS SYUKKAKAISYA_CD /* 2 出荷会社コード */ ,M4.MEISYO_NM AS SYUKKAKAISYA_NM /* 3 名称名(出荷会社名) */ ,M4.MEISYOKANA_NM AS SYUKKAKAISYA_NM_KANA /* 4 名称名(出荷会社名) */ ,T1.SYUKKATEN_CD AS SYUKKATEN_CD /* 5 出荷店コード */ ,M1.TENPO_NM AS TENPO_NM /* 6 店舗名 */ ,T1.DENPYO_BANGO AS DENPYO_BANGO /* 7 伝票番号 */ ,T1.SYUKKA_YMD AS SYUKKA_YMD /* 8 出荷日 */ ,T1.JYURYOKAISYA_CD AS JYURYOKAISYA_CD /* 9 入荷会社コード */ ,T1.JYURYOTEN_CD AS JYURYOTEN_CD /* 10 入荷店コード */ ,NULL AS JYURYO_YMD /* 11 入荷日 */ ,T1.SCM_CD AS SCM_CD /* 12 SCMコード */ ,T1.NYUKAYOTEIGOKEI_SU AS NYUKAYOTEIGOUKEI_SU /* 13 入荷予定合計数量 */ ,T1.NYUKAYOTEIGOKEI_KIN AS NYUKAYOTEIGOUKEI_KIN /* 14 入荷予定合計金額 */ ,NULL AS NYUKAJISSEKIGOUKEI_SU /* 15 入荷実績合計数量 */ ,NULL AS NYUKAJISSEKIGOUKEI_KIN /* 16 入荷実績合計金額 */ ,T1.SAI_FLG AS SAI_FLG /* 17 差異フラグ */ ,DECODE(T1.DENPYO_JYOTAI,0,2,1,1) AS DENPYO_JYOTAI /* 18 伝票状態 */ ,M5.MEISYO_NM AS DENPYO_NM /* 19 伝票状態名称 */ ,T1.SYUKKA_SIJI_NO AS SYUKKA_SIJI_NO /* 20 出荷指示NO */ ,T1.SYUKKATAN_CD AS SYUKKATAN_CD /* 21 出荷担当者コード */ ,M3.HANBAIIN_NM AS SYUKKATAN_HANBAIIN_NM /* 22 出荷担当者名称 */ ,T1.IDORIYU_KB AS IDORIYU_KB /* 23 移動指示理由 */ ,CASE WHEN T1.IDORIYU_KB =1 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS KYAKUTYU_FLG /* 24 客注フラグ */ ,CASE WHEN T1.IDORIYU_KB =2 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS NEGAKIHIN_FLG /* 25 値書きフラグ */ ,T1.ADD_YMD AS ADD_YMD /* 26 登録日 */ ,T1.ADD_TM AS ADD_TM /* 27 登録時間 */ ,NULL AS ADDHANBAIIN_CD /* 28 登録担当者コード(入荷担当者コード) */ ,NULL AS HANBAIIN_NM /* 29 担当者名称(入荷担当者コード) */ ,T1.UPD_YMD AS UPD_YMD /* 30 更新日 */ ,T1.UPD_TM AS UPD_TM /* 31 更新時間 */ ,T1.UPD_TANCD AS UPD_TANCD /* 32 更新担当者コード */ ,T1.KAKUTEI_FLG AS KAKUTEI_FLG /* 33 確定フラグ */ ,T1.SAKUJYO_YMD AS SAKUJYO_YMD /* 34 削除日 */ ,T1.SAKUJYO_FLG AS SAKUJYO_FLG /* 35 削除フラグ */ ,NULL AS SOSINIRAI_FLG /* 36 送信依頼フラグ */ ,NULL AS SOSINZUMI_FLG /* 37 送信済フラグ */ ,NULL AS SOSIN_YMD /* 38 送信日 */ ,NULL AS SOSIN_TM /* 39 送信時間 */ ,NULL AS SYORIZUMI_FLG /* 40 処理済フラグ */ ,T1.JYUSIN_YMD AS JYUSIN_YMD /* 41 受信日 */ ,T1.JYUSIN_TM AS JYUSIN_TM /* 42 受信時間 */ ,NULL AS KEIHI_DENPYO_BANGO /* 43 経費伝票番号 */ ,T1.HHTSERIAL_NO AS HHTSERIAL_NO /* 44 HHTシリアル番号 */ ,T1.HHTSEQUENCE_NO AS HHTSEQUENCE_NO /* 45 HHTシーケンスNO. */ ,NULL AS RIREKI_SYORI_YMD /* 46 履歴処理日付 */ ,NULL AS RIREKI_SYORI_TM /* 47 履歴処理時間 */ ,0 AS RIREKI_NO /* 48 履歴No */ ,0 AS AKAKURO_KBN /* 49 赤黒区分 */ ,NULL AS KAKUTEI_SB /* 50 確定種別 */ , DECODE(T1.SYUKKAKAISYA_CD, , 0 , T1.SYUKKAKAISYA_CD) AS SORT ,AS LOGIN_KAISYA_CD /* 51 ログイン会社コード */ FROM
LEFT JOIN BOMT0010_ALL M1 ON T1.SYUKKAKAISYA_CD = M1.KAISYA_CD AND T1.SYUKKATEN_CD = M1.TENPO_CD LEFT JOIN BOMT0010 M2 ON T1.JYURYOTEN_CD = M2.TENPO_CD LEFT JOIN BOMT0030_ALL M3 ON T1.SYUKKAKAISYA_CD = CAST(M3.KAISYA_CD AS NUMBER(2)) AND T1.SYUKKATAN_CD = M3.HANBAIIN_CD LEFT JOIN MDMT0100 M4 ON T1.SYUKKAKAISYA_CD = M4.MEISYO_CD AND M4.SIKIBETSU_CD = 'KASY' LEFT JOIN MDMT0100 M5 ON M5.MEISYO_CD = '2' AND M5.SIKIBETSU_CD = 'IDST' WHERE 0=0
UNION ALL SELECT T1.TENPOLC_KBN AS TENPOLC_KBN /* 1 店舗ＬＣ区分 */ ,T1.SYUKKAKAISYA_CD AS SYUKKAKAISYA_CD /* 2 出荷会社コード */ ,M5.MEISYO_NM AS SYUKKAKAISYA_NM /* 3 名称名(出荷会社名) */ ,M5.MEISYOKANA_NM AS SYUKKAKAISYA_NM_KANA /* 4 名称名(出荷会社名) */ ,T1.SYUKKATEN_CD AS SYUKKATEN_CD /* 5 出荷店コード */ ,M1.TENPO_NM AS TENPO_NM /* 6 店舗名 */ ,T1.DENPYO_BANGO AS DENPYO_BANGO /* 7 伝票番号 */ ,T1.SYUKKA_YMD AS SYUKKA_YMD /* 8 出荷日 */ ,T1.JYURYOKAISYA_CD AS JYURYOKAISYA_CD /* 9 入荷会社コード */ ,T1.JYURYOTEN_CD AS JYURYOTEN_CD /* 10 入荷店コード */ ,T1.JYURYO_YMD AS JYURYO_YMD /* 11 入荷日 */ ,T1.SCM_CD AS SCM_CD /* 12 SCMコード */ ,T1.NYUKAYOTEIGOUKEI_SU AS NYUKAYOTEIGOUKEI_SU /* 13 入荷予定合計数量 */ ,T1.NYUKAYOTEIGOUKEI_KIN AS NYUKAYOTEIGOUKEI_KIN /* 14 入荷予定合計金額 */ ,T1.NYUKAJISSEKIGOUKEI_SU AS NYUKAJISSEKIGOUKEI_SU /* 15 入荷実績合計数量 */ ,T1.NYUKAJISSEKIGOUKEI_KIN AS NYUKAJISSEKIGOUKEI_KIN /* 16 入荷実績合計金額 */ ,T1.SAI_FLG AS SAI_FLG /* 17 差異フラグ */ ,1 AS DENPYO_JYOTAI /* 18 伝票状態 */ ,M6.MEISYO_NM AS DENPYO_NM /* 19 伝票状態名称 */ ,T1.SYUKKA_SIJI_NO AS SYUKKA_SIJI_NO /* 20 出荷指示NO */ ,T1.SYUKKATAN_CD AS SYUKKATAN_CD /* 21 出荷担当者コード */ ,M3.HANBAIIN_NM AS SYUKKATAN_HANBAIIN_NM /* 22 出荷担当者名称 */ ,T1.IDORIYU_KB AS IDORIYU_KB /* 23 移動指示理由 */ ,CASE WHEN T1.IDORIYU_KB =1 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS KYAKUTYU_FLG /* 24 客注フラグ */ ,CASE WHEN T1.IDORIYU_KB =2 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS NEGAKIHIN_FLG /* 25 値書きフラグ */ ,T1.ADD_YMD AS ADD_YMD /* 26 登録日 */ ,T1.ADD_TM AS ADD_TM /* 27 登録時間 */ ,T1.ADDTAN_CD AS ADDHANBAIIN_CD /* 28 登録担当者コード */ ,M4.HANBAIIN_NM AS HANBAIIN_NM /* 29 担当者名称 */ ,T1.UPD_YMD AS UPD_YMD /* 30 更新日 */ ,T1.UPD_TM AS UPD_TM /* 31 更新時間 */ ,T1.UPD_TANCD AS UPD_TANCD /* 32 更新担当者コード */ ,1 AS KAKUTEI_FLG /* 33 確定フラグ */ ,T1.SAKUJYO_YMD AS SAKUJYO_YMD /* 34 削除日 */ ,T1.SAKUJYO_FLG AS SAKUJYO_FLG /* 35 削除フラグ */ ,T1.SOSINIRAI_FLG AS SOSINIRAI_FLG /* 36 送信依頼フラグ */ ,T1.SOSINZUMI_FLG AS SOSINZUMI_FLG /* 37 送信済フラグ */ ,T1.SOSIN_YMD AS SOSIN_YMD /* 38 送信日 */ ,T1.SOSIN_TM AS SOSIN_TM /* 39 送信時間 */ ,T1.SYORIZUMI_FLG AS SYORIZUMI_FLG /* 40 処理済フラグ */ ,NULL AS JYUSIN_YMD /* 41 受信日 */ ,NULL AS JYUSIN_TM /* 42 受信時間 */ ,T1.KEIHI_DENPYO_BANGO AS KEIHI_DENPYO_BANGO /* 43 経費伝票番号 */ ,T1.HHTSERIAL_NO AS HHTSERIAL_NO /* 44 HHTシリアル番号 */ ,T1.HHTSEQUENCE_NO AS HHTSEQUENCE_NO /* 45 HHTシーケンスNO. */ ,NULL AS RIREKI_SYORI_YMD /* 46 履歴処理日付 */ ,NULL AS RIREKI_SYORI_TM /* 47 履歴処理時間 */ ,NULL AS RIREKI_NO /* 48 履歴No */ ,NULL AS AKAKURO_KBN /* 49 赤黒区分 */ ,NULL AS KAKUTEI_SB /* 50 確定種別 */ , DECODE(T1.SYUKKAKAISYA_CD, , 0 , T1.SYUKKAKAISYA_CD) AS SORT ,AS LOGIN_KAISYA_CD /* 51 ログイン会社コード */ FROM
LEFT JOIN BOMT0010_ALL M1 ON T1.SYUKKAKAISYA_CD = M1.KAISYA_CD AND T1.SYUKKATEN_CD = M1.TENPO_CD LEFT JOIN BOMT0010 M2 ON T1.SYUKKATEN_CD = M2.TENPO_CD LEFT JOIN BOMT0030_ALL M3 ON T1.SYUKKAKAISYA_CD = CAST(M3.KAISYA_CD AS NUMBER(2)) AND T1.SYUKKATAN_CD = M3.HANBAIIN_CD LEFT JOIN BOMT0030 M4 ON T1.ADDTAN_CD = M4.HANBAIIN_CD LEFT JOIN MDMT0100 M5 ON T1.SYUKKAKAISYA_CD = M5.MEISYO_CD AND M5.SIKIBETSU_CD ='KASY' LEFT JOIN MDMT0100 M6 ON M6.MEISYO_CD = '1' AND M6.SIKIBETSU_CD = 'IDST' WHERE 0=0
ORDER BY SORT ,DENPYO_JYOTAI ,SYUKKATEN_CD ,SYUKKA_YMD ,DENPYO_BANGO
