ORDER BY DECODE(T1.SYUKKAKAISYA_CD, , 0 , T1.SYUKKAKAISYA_CD) ,T1.SYUKKATEN_CD ,T1.SYUKKA_YMD ,T1.DENPYO_BANGO
/** 仕入入荷検索(履歴テーブル) **/
SELECT T1.TENPOLC_KBN AS TENPOLC_KBN /* 1 店舗ＬＣ区分 */ ,T1.SYUKKAKAISYA_CD AS SYUKKAKAISYA_CD /* 2 出荷会社コード */ ,M5.MEISYO_NM AS SYUKKAKAISYA_NM /* 3 名称名(出荷会社名) */ ,M5.MEISYOKANA_NM AS SYUKKAKAISYA_NM_KANA /* 4 出荷会社名カナ */ ,T1.SYUKKATEN_CD AS SYUKKATEN_CD /* 5 出荷店コード */ ,M1.TENPO_NM AS TENPO_NM /* 6 店舗名 */ ,T1.DENPYO_BANGO AS DENPYO_BANGO /* 7 伝票番号 */ ,T1.SYUKKA_YMD AS SYUKKA_YMD /* 8 出荷日 */ ,T1.RIREKI_NO AS RIREKI_NO /* 9 履歴NO */ ,T1.AKAKURO_KBN AS AKAKURO_KBN /* 10 赤黒区分 */ ,T1.RIREKI_SYORI_YMD AS RIREKI_SYORI_YMD /* 11 履歴処理日付 */ ,T1.RIREKI_SYORI_TM AS RIREKI_SYORI_TM /* 12 履歴処理時間 */ ,T1.JYURYOKAISYA_CD AS JYURYOKAISYA_CD /* 13 入荷会社コード */ ,T1.JYURYOTEN_CD AS JYURYOTEN_CD /* 14 入荷店コード */ ,T1.SCM_CD AS SCM_CD /* 15 SCMコード */ ,T1.JYURYO_YMD AS JYURYO_YMD /* 16 入荷日 */ ,T1.NYUKAYOTEIGOUKEI_SU AS NYUKAYOTEIGOUKEI_SU /* 17 入荷予定合計数量 */ ,T1.NYUKAYOTEIGOUKEI_KIN AS NYUKAYOTEIGOUKEI_KIN /* 18 入荷予定合計金額 */ ,T1.NYUKAJISSEKIGOUKEI_SU AS NYUKAJISSEKIGOUKEI_SU /* 19 入荷実績合計数量 */ ,T1.NYUKAJISSEKIGOUKEI_KIN AS NYUKAJISSEKIGOUKEI_KIN /* 20 入荷実績合計金額 */ ,T1.SAI_FLG AS SAI_FLG /* 21 差異フラグ */ ,1 AS KAKUTEI_FLG /* 22 確定フラグ */ ,1 AS DENPYO_JYOTAI /* 23 伝票状態 */ ,M6.MEISYO_NM AS DENPYO_NM /* 24 伝票状態名称 */ ,T1.SYUKKA_SIJI_NO AS SYUKKA_SIJI_NO /* 25 出荷指示NO */ ,T1.SYUKKATAN_CD AS SYUKKATAN_CD /* 26 出荷担当者コード */ ,M3.HANBAIIN_NM AS SYUKKATAN_HANBAIIN_NM /* 27 出荷担当者名称 */ ,T1.IDORIYU_KB AS IDORIYU_KB /* 28 移動指示理由 */ ,CASE WHEN T1.IDORIYU_KB =1 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS KYAKUTYU_FLG /* 29 客注フラグ */ ,CASE WHEN T1.IDORIYU_KB =2 or T1.IDORIYU_KB =9 THEN '○' ELSE '' END AS NEGAKIHIN_FLG /* 30 値書きフラグ */ ,T1.SYORI_SB AS SYORI_SB /* 31 処理種別 */ ,M7.MEISYO_NM AS SYORI_NM /* 32 処理名 */ ,T1.RIREKI_DISP_KB AS RIREKI_DISP_KB /* 33 履歴画面表示区分 */ ,T1.ADD_YMD AS ADD_YMD /* 34 登録日 */ ,T1.ADD_TM AS ADD_TM /* 35 登録時間 */ ,T1.ADDTAN_CD AS ADDHANBAIIN_CD /* 36 登録担当者コード */ ,M4.HANBAIIN_NM AS HANBAIIN_NM /* 37 担当者名称(入荷担当者コード) */ ,T1.UPD_YMD AS UPD_YMD /* 38 更新日 */ ,T1.UPD_TM AS UPD_TM /* 39 更新時間 */ ,T1.UPD_TANCD AS UPD_TANCD /* 40 更新担当者コード */ ,T1.SAKUJYO_YMD AS SAKUJYO_YMD /* 41 削除日 */ ,T1.SAKUJYO_FLG AS SAKUJYO_FLG /* 42 削除フラグ */ ,T1.SYORIZUMI_FLG AS SYORIZUMI_FLG /* 43 処理済フラグ */ ,T1.HHTSERIAL_NO AS HHTSERIAL_NO /* 44 HHTシリアル番号 */ ,T1.HHTSEQUENCE_NO AS HHTSEQUENCE_NO /* 45 HHTシーケンスNO. */ ,NULL AS KAKUTEI_SB /* 46 確定種別 */ ,AS LOGIN_KAISYA_CD /* 47 ログイン会社コード */ FROM
LEFT JOIN BOMT0010_ALL M1 ON T1.SYUKKAKAISYA_CD = M1.KAISYA_CD AND T1.SYUKKATEN_CD = M1.TENPO_CD LEFT JOIN BOMT0010 M2 ON T1.JYURYOTEN_CD = M2.TENPO_CD LEFT JOIN BOMT0030_ALL M3 ON T1.SYUKKAKAISYA_CD = CAST(M3.KAISYA_CD AS NUMBER(2)) AND T1.SYUKKATAN_CD = M3.HANBAIIN_CD LEFT JOIN BOMT0030 M4 ON T1.ADDTAN_CD = M4.HANBAIIN_CD LEFT JOIN MDMT0100 M5 ON T1.SYUKKAKAISYA_CD = M5.MEISYO_CD AND M5.SIKIBETSU_CD = 'KASY' LEFT JOIN MDMT0100 M6 ON M6.MEISYO_CD = '1' AND M6.SIKIBETSU_CD = 'IDST' LEFT JOIN MDMT0100 M7 ON T1.SYORI_SB = M7.MEISYO_CD AND M7.SIKIBETSU_CD = 'IDSY' WHERE 0=0
ORDER BY DECODE(T1.SYUKKAKAISYA_CD, , 0 , T1.SYUKKAKAISYA_CD) ,T1.SYUKKATEN_CD ,T1.SYUKKA_YMD ,T1.DENPYO_BANGO ,T1.RIREKI_SYORI_YMD ,T1.RIREKI_SYORI_TM
/** 移動入荷予定テーブル **/ /** 仕入入荷確定明細 検索(予定テーブル) **/
