/** 経費振替確定一覧 検索(申請テーブル) **/
SELECT T1.APPLY_YMD AS APPLY_YMD /* 申請日 */ , 0 AS KAKUTEI_YMD /* 確定日 */ , T1.DENPYO_BANGO AS DENPYO_BANGO /* 伝票番号 */ , T1.KAMOKU_CD AS KAMOKU_CD /* 科目コード */ , M2.KAMOKU_NM AS KAMOKU_NM /* 科目名 */ , ( SELECT SUM(T1_1.SURYO) FROM MDAT0021 T1_1 WHERE T1.DENPYO_BANGO = T1_1.DENPYO_BANGO AND T1.SYORI_YMD = T1_1.SYORI_YMD AND T1.TENPO_CD = T1_1.TENPO_CD ) AS SUURYO /* 数量 */ , ( SELECT SUM(T1_2.SURYO * NVL(M1_1.HYOKA_TNK, 0)) FROM MDAT0021 T1_2 INNER JOIN MDMT0130 M1_1 ON T1_2.JAN_CD = M1_1.JAN_CD WHERE T1.DENPYO_BANGO = T1_2.DENPYO_BANGO AND T1.SYORI_YMD = T1_2.SYORI_YMD AND T1.TENPO_CD = T1_2.TENPO_CD ) AS MONEY /* 原価金額 */ , ( SELECT SUM(T1_3.SURYO * T1_3.BAIKA_TNK) FROM MDAT0021 T1_3 WHERE T1.DENPYO_BANGO = T1_3.DENPYO_BANGO AND T1.SYORI_YMD = T1_3.SYORI_YMD AND T1.TENPO_CD = T1_3.TENPO_CD ) AS BAIKAMONEY /* 売価金額 */ , M1.HANBAIIN_NM AS HANBAIIN_NM /* 申請担当者名 */ , ( SELECT M1_2.MEISYO_NM FROM MDMT0100 M1_2 WHERE M1_2.SIKIBETSU_CD = 'KHSN' AND M1_2.MEISYO_CD = 3 ) AS SYONINSTATUS /* 承認種別名 */ , T1.SINSEIRIYU AS SINSEIRIYU /* 申請理由 */ , '' AS KYAKKARIYU /* 却下理由 */ , T1.SINSEITAN_CD AS SINSEITAN_CD /* 申請担当者コード */ , T1.SINSEIRIYU_KB AS SINSEIRIYU_KB /* 申請理由区分コード */ , T1.SYORI_YMD AS SYORI_YMD /* 処理日 */ , T1.ADD_YMD AS ADD_YMD /* 登録日 */ , T1.ADD_TM AS ADD_TM /* 登録時間 */ , T1.ADDTAN_CD AS ADDTAN_CD /* 登録担当者コード */ , T1.UPD_YMD AS UPD_YMD /* 更新日 */ , T1.UPD_TM AS UPD_TM /* 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 更新担当者コード */ , T1.TENPO_CD AS TENPO_CD /* 申請店舗コード */ , M3.TENPO_NM AS TENPO_NM /* 申請店舗名 */ , '' AS KAKUTEITANTOCD /* 確定担当者コード */ , '' AS KAKUTEITANTONM /* 確定担当者名 */ , 0 AS SYONINSTATUSKBN /* 承認状態 */ , T1.SYORI_TM AS SYORI_TM /* 処理時間 */ , 0 AS SOSINZUMI_FLG /* 送信済みフラグ */ , T1.TONANHINKANRI_NO AS TONANHINKANRI_NO /* 盗難品管理番号 */ , T1.TONANHINSYORI_YMD AS TONANHINSYORI_YMD/* 盗難品処理日付 */ , T1.TONANHINTENPO_CD AS TONANHINTENPO_CD /* 盗難品店舗コード */ , T1.JYURI_NO AS JYURI_NO /* 受理番号 */ , T1.GYOMURINGI_NO AS GYOMURINGI_NO /* 業務稟議No */ , M4.MEISYO_NM AS MEISYO_NM /* 申請理由区分名 */ , 1 AS TBLFLG /* テーブル区分 */ FROM
LEFT JOIN BOMT0030 M1 /* 担当者マスタ */ ON M1.HANBAIIN_CD = T1.SINSEITAN_CD LEFT JOIN MDMT0040 M2 /* 科目マスタ */ ON M2.KAMOKU_CD = T1.KAMOKU_CD LEFT JOIN BOMT0010 M3 /* 店舗マスタ */ ON M3.TENPO_CD = T1.TENPO_CD LEFT JOIN MDMT0100 M4 /* 名称マスタ */ ON M4.SIKIBETSU_CD = 'KHRY' AND M4.MEISYO_CD = T1.SINSEIRIYU_KB WHERE 0=0
/** 経費振替確定一覧 検索(確定テーブル) **/
SELECT T1.APPLY_YMD AS APPLY_YMD /* 申請日 */ , T1.UPD_YMD AS KAKUTEI_YMD /* 確定日 */ , T1.DENPYO_BANGO AS DENPYO_BANGO /* 伝票番号 */ , T1.KAMOKU_CD AS KAMOKU_CD /* 科目コード */ , M2.KAMOKU_NM AS KAMOKU_NM /* 科目名 */ , ( SELECT SUM(T1_1.SURYO) FROM MDAT0031 T1_1 WHERE T1.DENPYO_BANGO = T1_1.DENPYO_BANGO AND T1.SYORI_YMD = T1_1.SYORI_YMD AND T1.TENPO_CD = T1_1.TENPO_CD ) AS SUURYO /* 数量 */ , ( SELECT SUM(T1_2.SURYO * T1_2.GEN_TNK) FROM MDAT0031 T1_2 WHERE T1.DENPYO_BANGO = T1_2.DENPYO_BANGO AND T1.SYORI_YMD = T1_2.SYORI_YMD AND T1.TENPO_CD = T1_2.TENPO_CD ) AS MONEY /* 原価金額 */ , ( SELECT SUM(T1_3.SURYO * T1_3.BAIKA_TNK) FROM MDAT0031 T1_3 WHERE T1.DENPYO_BANGO = T1_3.DENPYO_BANGO AND T1.SYORI_YMD = T1_3.SYORI_YMD AND T1.TENPO_CD = T1_3.TENPO_CD ) AS BAIKAMONEY /* 売価金額 */ , M1.HANBAIIN_NM AS HANBAIIN_NM /* 申請担当者名 */ , M3.MEISYO_NM AS SYONINSTATUS /* 承認種別名 */ , T1.SINSEIRIYU AS SINSEIRIYU /* 申請理由 */ , T1.KYAKKARIYU AS KYAKKARIYU /* 却下理由 */ , T1.SINSEITAN_CD AS SINSEITAN_CD /* 申請担当者コード */ , T1.SINSEIRIYU_KB AS SINSEIRIYU_KB /* 申請理由区分コード */ , T1.SYORI_YMD AS SYORI_YMD /* 処理日 */ , T1.ADD_YMD AS ADD_YMD /* 登録日 */ , T1.ADD_TM AS ADD_TM /* 登録時間 */ , T1.ADDTAN_CD AS ADDTAN_CD /* 登録担当者コード */ , T1.UPD_YMD AS UPD_YMD /* 更新日 */ , T1.UPD_TM AS UPD_TM /* 更新時間 */ , T1.UPD_TANCD AS UPD_TANCD /* 更新担当者コード */ , T1.TENPO_CD AS TENPO_CD /* 申請店舗コード */ , M4.TENPO_NM AS TENPO_NM /* 申請店舗名 */ , T1.UPD_TANCD AS KAKUTEITANTOCD /* 確定担当者コード */ , M5.HANBAIIN_NM AS KAKUTEITANTONM /* 確定担当者名 */ , T1.SYONIN_FLG AS SYONINSTATUSKBN /* 承認状態 */ , T1.SYORI_TM AS SYORI_TM /* 処理時間 */ , T1.SOSINZUMI_FLG AS SOSINZUMI_FLG /* 送信済みフラグ */ , T1.TONANHINKANRI_NO AS TONANHINKANRI_NO /* 盗難品管理番号 */ , T1.TONANHINSYORI_YMD AS TONANHINSYORI_YMD /* 盗難品処理日付 */ , T1.TONANHINTENPO_CD AS TONANHINTENPO_CD /* 盗難品店舗コード */ , T1.JYURI_NO AS JYURI_NO /* 受理番号 */ , T1.GYOMURINGI_NO AS GYOMURINGI_NO /* 業務稟議No */ , M6.MEISYO_NM AS MEISYO_NM /* 申請理由区分名 */ , 2 AS TBLFLG /* テーブル区分 */ FROM
LEFT JOIN BOMT0030 M1 /* 担当者マスタ */ ON M1.HANBAIIN_CD = T1.SINSEITAN_CD LEFT JOIN MDMT0040 M2 /* 科目マスタ */ ON M2.KAMOKU_CD = T1.KAMOKU_CD LEFT JOIN MDMT0100 M3 /* 名称マスタ */ ON M3.SIKIBETSU_CD = 'KHSN' AND M3.MEISYO_CD = T1.SYONIN_FLG LEFT JOIN BOMT0010 M4 /* 店舗マスタ */ ON M4.TENPO_CD = T1.TENPO_CD LEFT JOIN BOMT0030 M5 /* 担当者マスタ */ ON M5.HANBAIIN_CD = T1.UPD_TANCD LEFT JOIN MDMT0100 M6 /* 名称マスタ */ ON M6.SIKIBETSU_CD = 'KHRY' AND M6.MEISYO_CD = T1.SINSEIRIYU_KB WHERE 0=0
