/** 棚卸状況確認(V) 照会(件数チェック) **/
SELECT COUNT(1) CNT FROM ( SELECT TENCD ,TENPO_NM ,ADD_YMD ,SOSIN_YMD ,SOSINIRAI_FLG ,SOSINIRAI_NM ,SOSINZUMI_FLG ,SOSINZUMI_NM FROM ( SELECT MDIT0020.TENCD TENCD ,MAX(BOMT0010.TENPO_NM) TENPO_NM ,MDIT0010.ADD_YMD ADD_YMD ,MAX(MDIT0020.SOSIN_YMD) SOSIN_YMD ,MAX(MDIT0020.SOSINIRAI_FLG) SOSINIRAI_FLG ,MAX(MDMT0100_1.MEISYO_NM) SOSINIRAI_NM ,MAX(TO_CHAR(MDIT0020.SOSINZUMI_FLG)) SOSINZUMI_FLG ,MAX(MDMT0100_2.MEISYO_NM) SOSINZUMI_NM ,MAX(MDIT0020.TANAOROSIKIJUN_YMD) TANAOROSIKIJUN_YMD ,1 SORTFLG FROM MDIT0020 /* 棚卸管理TBL */ LEFT JOIN MDMT0100 MDMT0100_1 /* 名称MST 店舗確定状況 */ ON MDMT0100_1.SIKIBETSU_CD = 'TNKK' AND MDIT0020.SOSINIRAI_FLG = MDMT0100_1.MEISYO_CD LEFT JOIN MDMT0100 MDMT0100_2 /* 名称MST 送信状況 */ ON MDMT0100_2.SIKIBETSU_CD = 'TNSS' AND DECODE(MDIT0020.SOSINZUMI_FLG,0,1,2) = MDMT0100_2.MEISYO_CD LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0020.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDIT0010 /* 棚卸確定TBL(H) */ ON MDIT0020.TENCD = MDIT0010.TENPO_CD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0010.TANAOROSI_YMD AND MDIT0020.SOSINKAI_SU = MDIT0010.SOSINKAI_SU WHERE 0=0
AND EXISTS ( SELECT 1 FROM MDIT0030 /* 棚卸実施日TBL */ WHERE MDIT0030.TANAOROSIKIJUN_YMD = MDIT0020.TANAOROSIKIJUN_YMD AND MDIT0030.TENCD = MDIT0020.TENCD
) GROUP BY MDIT0020.TENCD ,MDIT0010.ADD_YMD ,MDIT0020.SOSINKAI_SU
UNION ALL
SELECT MDIT0030.TENCD TENCD ,BOMT0010.TENPO_NM TENPO_NM ,0 ADD_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, MDIT0030.UPD_YMD, 0) SOSIN_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, 5, MDIT0030.TANAOROSISYURYO_FLG) SOSINIRAI_FLG ,MDMT0100_3.MEISYO_NM SOSINIRAI_NM ,'' SOSINZUMI_FLG ,'' SOSINZUMI_NM ,MDIT0030.TANAOROSIKIJUN_YMD TANAOROSIKIJUN_YMD ,2 SORTFLG FROM MDIT0030 /* 棚卸実施日TBL */ LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0030.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDMT0100 MDMT0100_3 /* 名称MST 終了状況 */ ON MDMT0100_3.SIKIBETSU_CD = 'TNSJ' AND MDIT0030.TANAOROSISYURYO_FLG = MDMT0100_3.MEISYO_CD WHERE EXISTS ( SELECT 1 FROM MDIT0020 WHERE 0=0
AND MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD ) AND MDIT0030.TENCD <> '9999'
AND EXISTS ( SELECT 1 FROM MDIT0020 LEFT JOIN MDIT0010 ON MDIT0020.TENCD = MDIT0010.TENPO_CD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0010.TANAOROSI_YMD AND MDIT0020.SOSINKAI_SU = MDIT0010.SOSINKAI_SU WHERE MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD
)
) )
/** 棚卸状況確認(V) 終了確認照会(件数チェック) **/
SELECT COUNT(1) CNT FROM ( SELECT MDIT0030.TENCD TENCD ,BOMT0010.TENPO_NM TENPO_NM ,0 ADD_YMD /* HHT実施日 */ ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, MDIT0030.UPD_YMD, 0) SOSIN_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, 5, MDIT0030.TANAOROSISYURYO_FLG) SOSINIRAI_FLG /* 棚卸終了フラグ*/ ,MDMT0100_3.MEISYO_NM SOSINIRAI_NM ,' ' SOSINZUMI_FLG ,' ' SOSINZUMI_NM ,MDIT0030.TANAOROSIKIJUN_YMD TANAOROSIKIJUN_YMD ,2 SORTFLG ,MDIT0030.TANAOROSIKAKUTEI_YMD TANAOROSIKAKUTEI_YMD FROM MDIT0030 /* 棚卸実施日TBL */ LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0030.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDMT0100 MDMT0100_3 /* 名称MST 終了状況 */ ON MDMT0100_3.SIKIBETSU_CD = 'TNSJ' AND MDIT0030.TANAOROSISYURYO_FLG = MDMT0100_3.MEISYO_CD WHERE EXISTS ( SELECT 1 FROM MDIT0020 WHERE 0=0
AND MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD ) AND MDIT0030.TENCD <> '9999'
AND EXISTS ( SELECT 1 FROM MDIT0020 /* 棚卸管理TBL */ LEFT JOIN MDIT0010 /* 棚卸確定TBL(H) */ ON MDIT0020.TENCD = MDIT0010.TENPO_CD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0010.TANAOROSI_YMD AND MDIT0020.SOSINKAI_SU = MDIT0010.SOSINKAI_SU WHERE MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD
)
)
/** 棚卸状況確認(V) 照会(検索ボタン) **/
SELECT TENCD /* 店舗コード */ ,TENPO_NM /* 店舗名称 */ ,NVL(ADD_YMD, 0) ADD_YMD /* HHT実施日 */ ,SOSIN_YMD /* 送信時間 */ ,SOSINIRAI_FLG /* 送信依頼フラグ */ ,NVL(SOSINIRAI_NM, ' ') SOSINIRAI_NM /* 店舗確定状況 */ ,SOSINZUMI_FLG /* 送信済みフラグ */ ,NVL(SOSINZUMI_NM, ' ') SOSINZUMI_NM /* 送信状況 */ ,0 TANAOROSIKAKUTEI_YMD /* 棚卸確定日(終了確認照会のみで使用するため固定0にする) */ FROM ( SELECT MDIT0020.TENCD TENCD ,MAX(BOMT0010.TENPO_NM) TENPO_NM ,MDIT0010.ADD_YMD ADD_YMD ,MAX(MDIT0020.SOSIN_YMD) SOSIN_YMD ,MAX(MDIT0020.SOSINIRAI_FLG) SOSINIRAI_FLG ,MAX(MDMT0100_1.MEISYO_NM) SOSINIRAI_NM ,MAX(TO_CHAR(MDIT0020.SOSINZUMI_FLG)) SOSINZUMI_FLG ,MAX(MDMT0100_2.MEISYO_NM) SOSINZUMI_NM ,MAX(MDIT0020.TANAOROSIKIJUN_YMD) TANAOROSIKIJUN_YMD ,1 SORTFLG FROM MDIT0020 /* 棚卸管理TBL */ LEFT JOIN MDMT0100 MDMT0100_1 /* 名称MST 店舗確定状況 */ ON MDMT0100_1.SIKIBETSU_CD = 'TNKK' AND MDIT0020.SOSINIRAI_FLG = MDMT0100_1.MEISYO_CD LEFT JOIN MDMT0100 MDMT0100_2 /* 名称MST 送信状況 */ ON MDMT0100_2.SIKIBETSU_CD = 'TNSS' AND DECODE(MDIT0020.SOSINZUMI_FLG,0,1,2) = MDMT0100_2.MEISYO_CD LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0020.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDIT0010 /* 棚卸確定TBL(H) */ ON MDIT0020.TENCD = MDIT0010.TENPO_CD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0010.TANAOROSI_YMD AND MDIT0020.SOSINKAI_SU = MDIT0010.SOSINKAI_SU WHERE 0=0
AND EXISTS ( SELECT 1 FROM MDIT0030 /* 棚卸実施日TBL */ WHERE MDIT0030.TANAOROSIKIJUN_YMD = MDIT0020.TANAOROSIKIJUN_YMD AND MDIT0030.TENCD = MDIT0020.TENCD
) GROUP BY MDIT0020.TENCD ,MDIT0010.ADD_YMD ,MDIT0020.SOSINKAI_SU
UNION ALL
SELECT MDIT0030.TENCD TENCD ,BOMT0010.TENPO_NM TENPO_NM ,0 ADD_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, MDIT0030.UPD_YMD, 0) SOSIN_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, 5, MDIT0030.TANAOROSISYURYO_FLG) SOSINIRAI_FLG ,MDMT0100_3.MEISYO_NM SOSINIRAI_NM ,' ' SOSINZUMI_FLG ,' ' SOSINZUMI_NM ,MDIT0030.TANAOROSIKIJUN_YMD TANAOROSIKIJUN_YMD ,2 SORTFLG FROM MDIT0030 /* 棚卸実施日TBL */ LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0030.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDMT0100 MDMT0100_3 /* 名称MST 終了状況 */ ON MDMT0100_3.SIKIBETSU_CD = 'TNSJ' AND MDIT0030.TANAOROSISYURYO_FLG = MDMT0100_3.MEISYO_CD WHERE EXISTS ( SELECT 1 FROM MDIT0020 WHERE 0=0
AND MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD ) AND MDIT0030.TENCD <> '9999'
AND EXISTS ( SELECT 1 FROM MDIT0020 LEFT JOIN MDIT0010 ON MDIT0020.TENCD = MDIT0010.TENPO_CD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0010.TANAOROSI_YMD AND MDIT0020.SOSINKAI_SU = MDIT0010.SOSINKAI_SU WHERE MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD
) )
/** 棚卸状況確認(V) 終了確認照会(検索ボタン) **/
SELECT TENCD /* 店舗コード */ ,TENPO_NM /* 店舗名称 */ ,NVL(ADD_YMD,0) ADD_YMD /* HHT実施日 */ ,SOSIN_YMD /* 送信時間 */ ,SOSINIRAI_FLG /* 送信依頼フラグ */ ,NVL(SOSINIRAI_NM,' ') SOSINIRAI_NM /* 店舗確定状況 */ ,SOSINZUMI_FLG /* 送信済みフラグ */ ,NVL(SOSINZUMI_NM,' ') SOSINZUMI_NM /* 送信状況 */ ,MAX(TANAOROSIKAKUTEI_YMD) OVER(PARTITION BY 1) TANAOROSIKAKUTEI_YMD /* 棚卸確定日 */ FROM ( SELECT MDIT0030.TENCD TENCD ,BOMT0010.TENPO_NM TENPO_NM ,0 ADD_YMD /* HHT実施日 */ ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, MDIT0030.UPD_YMD, 0) SOSIN_YMD ,DECODE(MDIT0030.TANAOROSISYURYO_FLG, 1, 5, MDIT0030.TANAOROSISYURYO_FLG) SOSINIRAI_FLG /* 棚卸終了フラグ */ ,MDMT0100_3.MEISYO_NM SOSINIRAI_NM ,' ' SOSINZUMI_FLG ,' ' SOSINZUMI_NM ,MDIT0030.TANAOROSIKIJUN_YMD TANAOROSIKIJUN_YMD ,2 SORTFLG ,MDIT0030.TANAOROSIKAKUTEI_YMD TANAOROSIKAKUTEI_YMD FROM MDIT0030 /* 棚卸実施日TBL */ LEFT JOIN BOMT0010 /* 店舗MST */ ON MDIT0030.TENCD = BOMT0010.TENPO_CD LEFT JOIN MDMT0100 MDMT0100_3 /* 名称MST 終了状況 */ ON MDMT0100_3.SIKIBETSU_CD = 'TNSJ' AND MDIT0030.TANAOROSISYURYO_FLG = MDMT0100_3.MEISYO_CD WHERE EXISTS ( SELECT 1 FROM MDIT0020 WHERE 0=0
AND MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD ) AND MDIT0030.TENCD <> '9999'
AND EXISTS ( SELECT 1 FROM MDIT0020 /* 棚卸管理TBL */ WHERE MDIT0020.TENCD = MDIT0030.TENCD AND MDIT0020.TANAOROSIKIJUN_YMD = MDIT0030.TANAOROSIKIJUN_YMD
) )
