/** 重複データ存在チェック */
SELECT AA.TENPO_CD ,AA.RINTANA_KANRI_NO ,AA.SYORI_YMD FROM ( SELECT A.TENPO_CD ,A.RINTANA_KANRI_NO ,A.SYORI_YMD ,A.BUMON_CD || A.HINSYU_SITEI_FLG || NVL(TO_CHAR(A.HINSYU_CD), '') || A.BURANDO_SITEI_FLG || NVL(A.BURANDO_CD1, '999999') || NVL(A.BURANDO_CD2, '999999') || NVL(A.BURANDO_CD3, '999999') || NVL(A.BURANDO_CD4, '999999') || NVL(A.BURANDO_CD5, '999999') || NVL(A.BURANDO_CD6, '999999') || NVL(A.BURANDO_CD7, '999999') || NVL(A.BURANDO_CD8, '999999') JOKEN ,B.JAN_CD FROM MDIT0120 A ,MDIT0121 B WHERE A.TENPO_CD = B.TENPO_CD AND A.RINTANA_KANRI_NO = B.RINTANA_KANRI_NO AND A.SYORI_YMD = B.SYORI_YMD
) AA ,( SELECT A.TENPO_CD ,A.RINTANA_KANRI_NO ,A.SYORI_YMD ,A.BUMON_CD || A.HINSYU_SITEI_FLG || NVL(TO_CHAR(A.HINSYU_CD), '') || A.BURANDO_SITEI_FLG || NVL(A.BURANDO_CD1, '999999') || NVL(A.BURANDO_CD2, '999999') || NVL(A.BURANDO_CD3, '999999') || NVL(A.BURANDO_CD4, '999999') || NVL(A.BURANDO_CD5, '999999') || NVL(A.BURANDO_CD6, '999999') || NVL(A.BURANDO_CD7, '999999') || NVL(A.BURANDO_CD8, '999999') JOKEN ,B.JAN_CD FROM MDIT0120 A ,MDIT0121 B WHERE A.TENPO_CD = B.TENPO_CD AND A.RINTANA_KANRI_NO = B.RINTANA_KANRI_NO AND A.SYORI_YMD = B.SYORI_YMD
) BB WHERE AA.TENPO_CD = BB.TENPO_CD AND AA.JOKEN <> BB.JOKEN AND AA.RINTANA_KANRI_NO <> BB.RINTANA_KANRI_NO AND AA.JAN_CD = BB.JAN_CD GROUP BY AA.TENPO_CD ,AA.RINTANA_KANRI_NO ,AA.SYORI_YMD ORDER BY AA.TENPO_CD ,AA.RINTANA_KANRI_NO ,AA.SYORI_YMD
