/** 臨時棚卸検索一覧(CSV)**/
SELECT B.TENPO_CD /* 1 店舗コード */ ,( SELECT BOMT0010.TENPO_NM FROM BOMT0010 WHERE BOMT0010.TENPO_CD = B.TENPO_CD ) TENPO_NM /* 2 店舗名 */ ,B.BUMON_CD /* 3 部門コード */ ,( SELECT BOMT0060.BUMONKANA_NM FROM BOMT0060 WHERE BOMT0060.BUMON_CD = B.BUMON_CD ) BUMONKANA_NM /* 4 部門カナ名 */ ,B.HINSYU_CD /* 5 品種コード */ ,( SELECT MDMT0070.HINSYU_RYAKU_NM FROM MDMT0070 WHERE MDMT0070.BUMON_CD = B.BUMON_CD AND MDMT0070.HINSYU_CD = B.HINSYU_CD ) HINSYU_RYAKU_NM /* 6 品種カナ名 */ ,B.BURANDO_CD /* 7 ブランドコード */ ,( SELECT STMT0500.BURANDO_NMK FROM STMT0500 WHERE STMT0500.BURANDO_CD = B.BURANDO_CD ) BURANDO_NMK /* 8 ブランド名 */ ,B.JISYA_HBN /* 9 自社品番 */ ,B.MAKER_HBN /* 10 メーカー品番 */ ,B.SYONMK /* 11 商品名(カナ) */ ,( SELECT MDMT0080.IRO_NM FROM MDMT0080 WHERE MDMT0080.IRO_CD = B.IRO_CD ) IRO_NM /* 12 色名 */ ,B.SIZE_NM /* 13 サイズ */ ,B.JAN_CD /* 14 ＪＡＮコード */ ,B.SYOHIN_CD /* 15 商品コード */ ,B.HYOKA_TNK /* 16 評価単価 */ ,B.TANAJITYOBO_SU /* 17 棚時帳簿数 */ ,B.TANAJISEKISO_SU /* 18 棚時積送数 */ ,B.JITANA_SU /* 19 実棚数 */ ,B.LOSS_SU /* 20 ロス数 */ ,B.LOSS_KIN /* 21 ロス金額 */ ,A.LOSSKEISAN_YMD /* 22 ロス計算日 */ ,A.LOSS_KANRI_NO /* 23 ロス管理No */ FROM MDIT0130 A ,MDIT0131 B WHERE A.TENPO_CD = B.TENPO_CD AND A.LOSS_KANRI_NO = B.LOSS_KANRI_NO AND A.SYORI_YMD = B.SYORI_YMD
ORDER BY B.TENPO_CD /* 店舗コード */ ,B.SYORI_YMD /* 処理日付 */ ,B.LOSS_KANRI_NO /* ロス管理No */ ,B.GYO_NO /* 行№ */
