/** 評価損確定(検索ボタン)件数チェック (確定モード) **/
SELECT COUNT(COUNT(1)) CNT FROM MDIT0060 /* 評価損申請 */ WHERE 0=0
AND MDIT0060.KESSAI_FLG = 0 AND MDIT0060.SHINSEI_FLG = 1 GROUP BY MDIT0060.TENPO_CD ,MDIT0060.APPLY_YMD ,MDIT0060.KESSAI_FLG
/** 評価損確定(検索ボタン)件数チェック (修正モード) **/
SELECT COUNT(COUNT(1)) CNT FROM MDIT0070 /* 評価損確定 */ LEFT JOIN MDIT0060 /* 評価損申請 */ ON MDIT0070.TENPO_CD = MDIT0060.TENPO_CD(+) AND MDIT0070.KANRI_NO = MDIT0060.KANRI_NO(+) AND MDIT0070.SYORI_YMD = MDIT0060.SYORI_YMD(+) AND MDIT0070.GYO_NBR = MDIT0060.GYO_NBR(+) WHERE 0 = 0
AND MDIT0070.SOSINZUMI_FLG = 0 AND MDIT0070.SAISHINSEIZUMI_FLG = 0 GROUP BY MDIT0070.TENPO_CD ,MDIT0070.APPLY_YMD ,MDIT0070.SYONIN_FLG
/** 評価損確定(検索ボタン)件数チェック (照会モード) **/
SELECT NVL(SUM(A),0) CNT FROM ( SELECT COUNT(COUNT(1)) A FROM MDIT0060 /* 評価損申請 */ WHERE 0 = 0
AND MDIT0060.KESSAI_FLG = 0 AND MDIT0060.SHINSEI_FLG = 1 GROUP BY MDIT0060.TENPO_CD ,MDIT0060.APPLY_YMD ,MDIT0060.KESSAI_FLG UNION ALL SELECT COUNT(COUNT(1)) A FROM MDIT0070 /* 評価損確定 */ LEFT JOIN MDIT0060 /* 評価損申請 */ ON MDIT0070.TENPO_CD = MDIT0060.TENPO_CD(+) AND MDIT0070.KANRI_NO = MDIT0060.KANRI_NO(+) AND MDIT0070.SYORI_YMD = MDIT0060.SYORI_YMD(+) AND MDIT0070.GYO_NBR = MDIT0060.GYO_NBR(+) WHERE 0 = 0
GROUP BY MDIT0070.TENPO_CD ,MDIT0070.APPLY_YMD ,MDIT0070.SYONIN_FLG )
/** 評価損確定(検索ボタン)検索 (確定モード) **/
SELECT MDIT0060.TENPO_CD TENPO_CD /* 1 店舗コード */ ,MAX(BOMT0010.TENPO_NM) TENPO_NM /* 2 店舗名 */ ,MDIT0060.APPLY_YMD APPLY_YMD /* 3 申請日 */ ,0 SYONIN_FLG /* 4 承認状態 */ ,'' SYONIN_FLG_NM /* 5 承認状態(名称) */ ,MDIT0060.KESSAI_FLG KESSAI_FLG /* 6 決裁状態 */ ,MAX(MDMT0100.MEISYO_NM) KESSAI_FLG_NM /* 7 決裁状態(名称) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE MDIT0060.HYOKASON_SU END) NOTNB_SURYO /* 8 数量(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE (MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) END) NOTNB_GENKA_KIN /* 9 原価金額(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN MDIT0060.HYOKASON_SU ELSE 0 END) NB_SURYO /* 10 数量(NB) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN (MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) ELSE 0 END) NB_GENKA_KIN /* 11 原価金額(NB) */ ,SUM(MDIT0060.HYOKASON_SU) TENPOGOKEI_SU /* 12 数量 */ ,SUM(MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) TENPOGOKEI_GENKA_KIN /* 13 原価金額 */ ,SUBSTR(MAX(MDIT0060.UPD_YMD || LPAD(MDIT0060.UPD_TM,9,'0')),0,8) UPD_UMD /* 14 更新日時(最大値) */ ,SUBSTR(MAX(MDIT0060.UPD_YMD || LPAD(MDIT0060.UPD_TM,9,'0')),9) UPD_TM /* 15 更新日内での最大値 */ ,MDIT0060.SAISHINSEI_FLG SAISHINSEI_FLG /* 16 再申請フラグ */ ,CASE WHEN MDIT0060.SAISHINSEI_FLG = 1 THEN '○' ELSE '' END SAISHINSEI_FLG_NM /* 16 再申請フラグ表示名 */ ,0 SOSINZUMI_FLG /* 18 送信済みフラグ */ FROM MDIT0060 /* 評価損申請テーブル */ ,BOMT0010 /* 店舗マスタ */ ,MDMT0100 /* 名称マスタ */ ,MDMT0130 /* 発注マスタ */ WHERE MDIT0060.TENPO_CD = BOMT0010.TENPO_CD(+) AND MDIT0060.KESSAI_FLG = MDMT0100.MEISYO_CD(+) AND MDIT0060.KESSAI_FLG = 0 AND MDIT0060.SHINSEI_FLG = 1 AND MDIT0060.JAN_CD = MDMT0130.JAN_CD(+) AND MDMT0100.SIKIBETSU_CD(+) = 'HKKS'
GROUP BY MDIT0060.TENPO_CD ,MDIT0060.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,MDIT0060.KESSAI_FLG ORDER BY MDIT0060.TENPO_CD ,MDIT0060.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,MDIT0060.KESSAI_FLG
/** 評価損確定(検索ボタン)検索 (修正モード) **/
SELECT MDIT0070.TENPO_CD TENPO_CD /* 1 店舗コード */ ,MAX(BOMT0010.TENPO_NM) TENPO_NM /* 2 店舗名 */ ,MDIT0070.APPLY_YMD APPLY_YMD /* 3 申請日 */ ,MDIT0070.SYONIN_FLG SYONIN_FLG /* 4 承認状態 */ ,MAX(MDMT0100_1.MEISYO_NM) SYONIN_FLG_NM /* 5 承認状態(名称) */ ,1 KESSAI_FLG /* 6 決裁状態 */ ,MAX(MDMT0100_2.MEISYO_NM) KESSAI_FLG_NM /* 7 決裁状態(名称) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE MDIT0070.HYOKASON_SU END) NOTNB_SURYO /* 8 数量(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE (MDIT0070.HYOKASON_SU * NVL(MDIT0070.GEN_TNK,'0')) END) NOTNB_GENKA_KIN /* 9 原価金額(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN MDIT0070.HYOKASON_SU ELSE 0 END) NB_SURYO /* 10 数量(NB) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN (MDIT0070.HYOKASON_SU * NVL(MDIT0070.GEN_TNK,'0')) ELSE 0 END) NB_GENKA_KIN /* 11 原価金額(NB) */ ,SUM(MDIT0070.HYOKASON_SU) TENPOGOKEI_SU /* 12 数量 */ ,SUM(MDIT0070.HYOKASON_SU * MDIT0070.GEN_TNK) TENPOGOKEI_GENKA_KIN /* 13 原価金額 */ ,SUBSTR(MAX(MDIT0070.UPD_YMD || LPAD(MDIT0070.UPD_TM,9,'0')),0,8) UPD_UMD /* 14 更新日時(最大値) */ ,SUBSTR(MAX(MDIT0070.UPD_YMD || LPAD(MDIT0070.UPD_TM,9,'0')),9) UPD_TM /* 15 更新日内での最大値 */ ,NVL(MDIT0060.SAISHINSEI_FLG,0) SAISHINSEI_FLG /* 16 再申請フラグ */ ,CASE WHEN MDIT0060.SAISHINSEI_FLG = 1 THEN '○' ELSE '' END SAISHINSEI_FLG_NM /* 16 再申請フラグ表示名 */ ,0 SOSINZUMI_FLG /* 18 送信済みフラグ */ FROM MDIT0070 /* 評価損確定テーブル */ ,MDIT0060 /* 評価損申請テーブル */ ,BOMT0010 /* 店舗マスタ */ ,MDMT0100 MDMT0100_1 /* 名称マスタ */ ,MDMT0100 MDMT0100_2 /* 名称マスタ(決裁状態) */ WHERE MDIT0070.TENPO_CD = BOMT0010.TENPO_CD(+) AND MDIT0070.SYONIN_FLG = MDMT0100_1.MEISYO_CD(+) AND MDIT0070.TENPO_CD = MDIT0060.TENPO_CD(+) AND MDIT0070.KANRI_NO = MDIT0060.KANRI_NO(+) AND MDIT0070.SYORI_YMD = MDIT0060.SYORI_YMD(+) AND MDIT0070.GYO_NBR = MDIT0060.GYO_NBR(+) AND MDIT0070.SOSINZUMI_FLG = 0 AND MDMT0100_1.SIKIBETSU_CD(+) = 'HKSN' AND MDMT0100_2.MEISYO_CD = 1 AND MDMT0100_2.SIKIBETSU_CD(+) = 'HKKS' AND MDIT0070.SAISHINSEIZUMI_FLG = 0
GROUP BY MDIT0070.TENPO_CD ,MDIT0070.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,MDIT0070.SYONIN_FLG ORDER BY MDIT0070.TENPO_CD ,MDIT0070.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,KESSAI_FLG ,MDIT0070.SYONIN_FLG
/** 評価損確定(検索ボタン)検索 (照会モード) **/
/* 申請テーブル */ SELECT MDIT0060.TENPO_CD TENPO_CD /* 1 店舗コード */ ,MAX(BOMT0010.TENPO_NM) TENPO_NM /* 2 店舗名 */ ,MDIT0060.APPLY_YMD APPLY_YMD /* 3 申請日 */ ,0 SYONIN_FLG /* 4 承認状態 */ ,'' SYONIN_FLG_NM /* 5 承認状態(名称) */ ,MDIT0060.KESSAI_FLG KESSAI_FLG /* 6 決裁状態 */ ,MAX(MDMT0100.MEISYO_NM) KESSAI_FLG_NM /* 7 決裁状態(名称) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE MDIT0060.HYOKASON_SU END) NOTNB_SURYO /* 8 数量(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE (MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) END) NOTNB_GENKA_KIN /* 9 原価金額(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN MDIT0060.HYOKASON_SU ELSE 0 END) NB_SURYO /* 10 数量(NB) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0060.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN (MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) ELSE 0 END) NB_GENKA_KIN /* 11 原価金額(NB) */ ,SUM(MDIT0060.HYOKASON_SU) TENPOGOKEI_SU /* 12 数量 */ ,SUM(MDIT0060.HYOKASON_SU * NVL(MDMT0130.HYOKA_TNK,'0')) TENPOGOKEI_GENKA_KIN /* 13 原価金額 */ ,SUBSTR(MAX(MDIT0060.UPD_YMD || LPAD(MDIT0060.UPD_TM,9,'0')),0,8) UPD_UMD /* 14 更新日時(最大値) */ ,SUBSTR(MAX(MDIT0060.UPD_YMD || LPAD(MDIT0060.UPD_TM,9,'0')),9) UPD_TM /* 15 更新日内での最大値 */ ,MDIT0060.SAISHINSEI_FLG SAISHINSEI_FLG /* 16 再申請フラグ */ ,CASE WHEN MDIT0060.SAISHINSEI_FLG = 1 THEN '○' ELSE '' END SAISHINSEI_FLG_NM /* 16 再申請フラグ表示名 */ ,0 SOSINZUMI_FLG /* 18 送信済みフラグ */ FROM MDIT0060 /* 評価損申請テーブル */ ,BOMT0010 /* 店舗マスタ */ ,MDMT0100 /* 名称マスタ */ ,MDMT0130 /* 発注マスタ */ WHERE MDIT0060.TENPO_CD = BOMT0010.TENPO_CD(+) AND MDIT0060.KESSAI_FLG = MDMT0100.MEISYO_CD(+) AND MDIT0060.KESSAI_FLG = 0 AND MDIT0060.SHINSEI_FLG = 1 AND MDIT0060.JAN_CD = MDMT0130.JAN_CD(+) AND MDMT0100.SIKIBETSU_CD(+) = 'HKKS'
GROUP BY MDIT0060.TENPO_CD ,MDIT0060.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,MDIT0060.KESSAI_FLG UNION ALL /* 確定テーブル */ SELECT MDIT0070.TENPO_CD TENPO_CD /* 1 店舗コード */ ,MAX(BOMT0010.TENPO_NM) TENPO_NM /* 2 店舗名 */ ,MDIT0070.APPLY_YMD APPLY_YMD /* 3 申請日 */ ,MDIT0070.SYONIN_FLG SYONIN_FLG /* 4 承認状態 */ ,MAX(MDMT0100_1.MEISYO_NM) SYONIN_FLG_NM /* 5 承認状態(名称) */ ,1 KESSAI_FLG /* 6 決裁状態 */ ,MAX(MDMT0100_2.MEISYO_NM) KESSAI_FLG_NM /* 7 決裁状態(名称) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE MDIT0070.HYOKASON_SU END) NOTNB_SURYO /* 8 数量(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN 0 ELSE (MDIT0070.HYOKASON_SU * NVL(MDIT0070.GEN_TNK,'0')) END) NOTNB_GENKA_KIN /* 9 原価金額(NB以外) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN MDIT0070.HYOKASON_SU ELSE 0 END) NB_SURYO /* 10 数量(NB) */ ,SUM(CASE WHEN TO_NUMBER(SUBSTR(MDIT0070.TYOTATSU_KB, -1)) BETWEEN 1 AND 4 THEN (MDIT0070.HYOKASON_SU * NVL(MDIT0070.GEN_TNK,'0')) ELSE 0 END) NB_GENKA_KIN /* 11 原価金額(NB) */ ,SUM(MDIT0070.HYOKASON_SU) TENPOGOKEI_SU /* 12 数量 */ ,SUM(MDIT0070.HYOKASON_SU * MDIT0070.GEN_TNK) TENPOGOKEI_GENKA_KIN /* 13 原価金額 */ ,SUBSTR(MAX(MDIT0070.UPD_YMD || LPAD(MDIT0070.UPD_TM,9,'0')),0,8) UPD_UMD /* 14 更新日時(最大値) */ ,SUBSTR(MAX(MDIT0070.UPD_YMD || LPAD(MDIT0070.UPD_TM,9,'0')),9) UPD_TM /* 15 更新日内での最大値 */ ,NVL(MDIT0060.SAISHINSEI_FLG,0) SAISHINSEI_FLG /* 16 再申請フラグ */ ,CASE WHEN MDIT0060.SAISHINSEI_FLG = 1 THEN '○' ELSE '' END SAISHINSEI_FLG_NM /* 16 再申請フラグ表示名 */ ,MIN(MDIT0070.SOSINZUMI_FLG) SOSINZUMI_FLG /* 18 送信済みフラグ */ FROM MDIT0070 /* 評価損確定テーブル */ ,MDIT0060 /* 評価損申請テーブル */ ,BOMT0010 /* 店舗マスタ */ ,MDMT0100 MDMT0100_1 /* 名称マスタ */ ,MDMT0100 MDMT0100_2 /* 名称マスタ(決裁状態) */ WHERE MDIT0070.TENPO_CD = BOMT0010.TENPO_CD(+) AND MDIT0070.SYONIN_FLG = MDMT0100_1.MEISYO_CD(+) AND MDIT0070.TENPO_CD = MDIT0060.TENPO_CD(+) AND MDIT0070.KANRI_NO = MDIT0060.KANRI_NO(+) AND MDIT0070.SYORI_YMD = MDIT0060.SYORI_YMD(+) AND MDIT0070.GYO_NBR = MDIT0060.GYO_NBR(+) AND MDMT0100_1.SIKIBETSU_CD(+) = 'HKSN' AND MDMT0100_2.MEISYO_CD = 1 AND MDMT0100_2.SIKIBETSU_CD(+) = 'HKKS'
GROUP BY MDIT0070.TENPO_CD ,MDIT0070.APPLY_YMD ,MDIT0060.SAISHINSEI_FLG ,MDIT0070.SYONIN_FLG ORDER BY TENPO_CD ,APPLY_YMD ,SAISHINSEI_FLG ,SYONIN_FLG
/** 評価損確定(店舗リンク)検索 (申請テーブル) **/
SELECT MDIT0060.TENPO_CD TENPO_CD /* 1 店舗コード */ ,MDIT0060.KANRI_NO KANRI_NO /* 2 管理NO */ ,MDIT0060.SYORI_YMD SYORI_YMD /* 3 処理日付 */ ,MDIT0060.SYORI_TM SYORI_TM /* 4 処理時間 */ ,MDIT0060.GYO_NBR GYO_NBR /* 5 行NO */ ,MDIT0060.JAN_CD JAN_CD /* 6 JANコード */ ,MDIT0060.HINSYU_CD HINSYU_CD /* 7 品種コード */ ,MDIT0060.MAKER_HBN MAKER_HBN /* 8 メーカー品番 */ ,MDIT0060.SYOHIN_CD SYOHIN_CD /* 9 商品コード */ ,MDIT0060.SYONMK SYONMK /* 10 商品名カナ */ ,MDIT0060.HANBAIKANRYO_YMD HANBAIKANRYO_YMD /* 11 販売完了日 */ ,MDIT0060.IRO_CD IRO_CD /* 12 色コード */ ,MDMT0080.IRO_NM IRO_NM /* 13 色名 */ ,MDIT0060.SIZE_CD SIZE_CD /* 14 サイズコード */ ,MDIT0060.SIZE_NM SIZE_NM /* 15 サイズ名 */ ,MDMT0130.HYOKA_TNK GEN_TNK /* 16 原単価 */ ,MDIT0060.HYOKASON_SU HYOKASON_SU /* 17 評価損数量 */ ,MDIT0060.HYOKASONSYUBETSU_KB HYOKASONSYUBETSU_KB /* 18 評価損種別区分 */ ,MDMT0100_1.MEISYO_NM HYOKASONSYUBETSU_KB_NM /* 19 評価損種別名 */ ,MDIT0060.HYOKASONRIYU_KB HYOKASONRIYU_KB /* 20 評価損理由区分 */ ,MDIT0060.HYOKASONRIYU HYOKASONRIYU /* 21 評価損理由 */ ,MDMT0100_2.MEISYO_NM HYOKASONRIYU_NM /* 22 評価損理由区分名称 */ ,'' KYAKKARIYU_KB /* 23 却下理由区分 */ ,'' KYAKKARIYU /* 24 却下理由 */ ,'' KYAKKARIYU_NM /* 25 却下理由区分名称 */ ,0 SYONIN_FLG /* 26 承認フラグ */ ,MDIT0060.BUMON_CD BUMON_CD /* 27 部門コード */ ,MDIT0060.JISYA_HBN JISYA_HBN /* 28 自社品番 */ ,MDIT0060.JODAI1_TNK JODAI1_TNK /* 29 上代1 */ ,MDIT0060.BURANDO_CD BURANDO_CD /* 30 ブランドコード */ ,MDIT0060.APPLY_YMD APPLY_YMD /* 31 申請日 */ ,MDIT0060.ADD_YMD ADD_YMD /* 32 登録日 */ ,MDIT0060.ADDTAN_CD ADDTAN_CD /* 33 登録担当者コード */ ,MDIT0060.UPD_TANCD UPD_TANCD /* 34 更新担当者コード */ ,BOMT0060.BUMON_NM BUMONNM /* 35 部門名 */ ,BOMT0060.BUMONKANA_NM BUMONKANA /* 36 部門名カナ */ ,STMT0500.BURANDO_NMK BURANDOKANA /* 37 ブランド名カナ */ ,MDIT0060.TYOTATSU_KB TYOTATSU_KB /* 38 調達区分 */ ,MDMT0070.HINSYU_RYAKU_NM HINSYU_RYAKU_NM /* 39 品種名 */ ,MDMT0100_3.MEISYO_NM TYOTATSU_KB_NM /* 40 調達区分名称 */ FROM MDIT0060 /* 評価損申請テーブル */ LEFT JOIN MDMT0080 /* 色マスタ */ ON MDIT0060.IRO_CD = MDMT0080.IRO_CD LEFT JOIN MDMT0070 /* 品種マスタ */ ON MDIT0060.BUMON_CD = MDMT0070.BUMON_CD AND MDIT0060.HINSYU_CD = MD